const fetchHotelImages = async () => {
  try {
    const imagesResponse = await Promise.all(
      hoteles.map(async (hotel) => {
        const response = await fetch(`http://localhost:8080/hoteles/${hotel.id_hotel}/imagenes`);
        return response.ok ? await response.json() : [];
      })
    );
    const imagesObject = hoteles.reduce((acc, hotel) => {
      acc[hotel.id_hotel] = []; // Inicializa cada hotel con un arreglo vacío
      return acc;
    }, {});
    setHotelImages(imagesObject);
  } catch (error) {
    console.error('Error fetching hotel images:', error);
  }
};



