import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import HotelSearchForm from './HotelSearchForm'; 

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();


  useEffect(() => {
    fetchHotelsAndRooms();
  }, []);


  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };


  const fetchHotelsAndRooms = async (country = '') => {
    const url = country ? `http://localhost:8080/hoteles?pais=${country}` : 'http://localhost:8080/hoteles';
    try {
      const response = await fetch(url);
      const data = await response.json();
      setHotels(data);
    } catch (error) {
      console.error('Error fetching hotels:', error);
    }
  };

  const onHotelesFiltrados = (searchParams) => {
    fetchHotelsAndRooms(searchParams.pais);
  };

  return (
    <Container className="my-5">
      <HotelSearchForm onSearch={onHotelesFiltrados} />
      {hotels.map((hotel) => (
        <Row key={hotel.id_hotel}>
          <Col md={12}>
            <h2>Hotel: {hotel.nombre}</h2>
            <p>{hotel.ciudad}, {hotel.pais}</p>
            <p>Dirección: {hotel.direccion}</p>
            {hotel.rooms.map((room) => (
              <Col key={room.id_habitacion} md={4}>
                <Card className="mb-3">
                  <Card.Body>
                    <Card.Title>Habitación: Tipo {room.tipo_habitacion}</Card.Title>
                    <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                    <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                    <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                    <Card.Img variant="top" src={defaultRoomImage} />
                    <Button variant="primary" onClick={() => navigate('/checkout', { state: { hotel, room } })}>
                      Reservar
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Col>
        </Row>
      ))}
    </Container>
  );
};

export default HomePage;
