import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar, Line, Pie } from 'react-chartjs-2';
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Analiticos = () => {
    const [data, setData] = useState({
        busquedas: [],
        paisesData: [],
        evolucionData: [],
        tipoAccesoData: { web: 0, rest: 0 }
    });

    useEffect(() => {
        fetchDatos();
    }, []);

    const fetchDatos = async () => {
        try {
            const response = await axios.get('/api/analiticos/registros');
            processData(response.data);
        } catch (error) {
            console.error('Error al cargar los datos de analítica:', error);
        }
    };

    const processData = (busquedas) => {
        const paisesData = {};
        const evolucionData = {};
        const tipoAccesoData = { web: 0, rest: 0 };

        busquedas.forEach(busqueda => {
            const { pais, tipoAcceso } = parseParametrosBusqueda(busqueda.parametrosBusqueda);
            paisesData[pais] = (paisesData[pais] || 0) + 1;
            tipoAccesoData[tipoAcceso] = (tipoAccesoData[tipoAcceso] || 0) + 1;
            const fecha = busqueda.fechaHora.split('T')[0];
            evolucionData[fecha] = (evolucionData[fecha] || 0) + 1;
        });

        setData({
            busquedas,
            paisesData: Object.entries(paisesData).map(([pais, cantidad]) => ({ pais, cantidad })),
            evolucionData: Object.entries(evolucionData).map(([fecha, cantidad]) => ({ fecha, cantidad })),
            tipoAccesoData
        });
    };

    const parseParametrosBusqueda = (parametrosBusqueda) => {
        const params = parametrosBusqueda.split('; ').reduce((acc, param) => {
            const [key, value] = param.split('=');
            acc[key] = value;
            return acc;
        }, {});
        return { pais: params.pais, tipoAcceso: params.tipoAcceso };
    };

    // Opciones y datos para gráficas
    const barData = {
        labels: data.paisesData.map(d => d.pais),
        datasets: [{
            label: 'Número de búsquedas por país',
            data: data.paisesData.map(d => d.cantidad),
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
        }],
    };

    const lineData = {
        labels: data.evolucionData.map(d => d.fecha),
        datasets: [{
            label: 'Búsquedas a lo largo del tiempo',
            data: data.evolucionData.map(d => d.cantidad),
            fill: false,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1,
        }],
    };

    const pieData = {
        labels: ['Web', 'REST'],
        datasets: [{
            label: 'Tipo de Acceso',
            data: [data.tipoAccesoData.web, data.tipoAccesoData.rest],
            backgroundColor: ['rgba(255, 205, 86, 0.7)', 'rgba(54, 162, 235, 0.7)'],
            hoverOffset: 4,
        }],
    };

    return (
        <Container fluid>
            <Row>
                <Col md={12}>
                    <h2>Análisis de Búsquedas</h2>
                </Col>
            </Row>
            <Row className="mt-4">
                <Col md={6}>
                    <Bar data={barData} options={{ responsive: true }} />
                </Col>
                <Col md={6}>
                    <Line data={lineData} options={{ scales: { y: { beginAtZero: true } } }} />
                </Col>
            </Row>
            <Row>
                <Col md={6}>
                    <h4>Distribución del Tipo de Acceso</h4>
                    <Pie data={pieData} options={{ responsive: true }} />
                </Col>
            </Row>
        </Container>
    );
};

export default Analiticos;
