import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import Comentarios from './Comentarios';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [fechaIngreso, setFechaIngreso] = useState('');
  const [fechaSalida, setFechaSalida] = useState('');
  const [numeroPersonas, setNumeroPersonas] = useState(1);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [hotelImages, setHotelImages] = useState({});

  useEffect(() => {
    fetchPaises();
  }, []);

  const fetchPaises = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles/pais');
      if (!response.ok) throw new Error('Error al cargar los países');
      const data = await response.json();
      setPaises(data);
    } catch (error) {
      setError('Error al cargar los países: ' + error.message);
    }
  };

  const buscarHabitacionesDisponibles = async () => {
    let url = `http://localhost:8080/habitaciones/buscar?pais=${paisSeleccionado}&fechaIngreso=${fechaIngreso}&fechaSalida=${fechaSalida}&numeroPersonas=${numeroPersonas}`;
    try {
      const response = await fetch(url);
      if (!response.ok) throw new Error('Error al buscar habitaciones disponibles');
      const habitacionesDisponibles = await response.json();
      setHotels(habitacionesDisponibles);
      habitacionesDisponibles.forEach(hotel => {
        fetchHotelImages(hotel.id_hotel);
      });
    } catch (error) {
      setError('Error al buscar habitaciones disponibles: ' + error.message);
    }
  };

  const fetchHotelImages = async (hotelId) => {
    try {
      const response = await fetch(`http://localhost:8080/hoteles/${hotelId}/imagenes`);
      if (!response.ok) throw new Error('Error al cargar imágenes del hotel');
      const images = await response.json();
      setHotelImages(prev => ({ ...prev, [hotelId]: images }));
    } catch (error) {
      console.error('Error fetching hotel images:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    buscarHabitacionesDisponibles();
  };

  return (
    <Container className="my-5">
      <Form onSubmit={handleSubmit}>
        <Row>
          <Col>
            <Form.Group controlId="pais">
              <Form.Label>País</Form.Label>
              <Form.Control as="select" value={paisSeleccionado} onChange={(e) => setPaisSeleccionado(e.target.value)}>
                <option>Seleccione un país</option>
                {paises.map(pais => (
                  <option key={pais} value={pais}>{pais}</option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="fechaIngreso">
              <Form.Label>Fecha de Ingreso</Form.Label>
              <Form.Control type="date" value={fechaIngreso} onChange={(e) => setFechaIngreso(e.target.value)} />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="fechaSalida">
              <Form.Label>Fecha de Salida</Form.Label>
              <Form.Control type="date" value={fechaSalida} onChange={(e) => setFechaSalida(e.target.value)} />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="numeroPersonas">
              <Form.Label>Número de Personas</Form.Label>
              <Form.Control type="number" min="1" value={numeroPersonas} onChange={(e) => setNumeroPersonas(e.target.value)} />
            </Form.Group>
          </Col>
        </Row>
        <Button type="submit" className="mb-2">
          Buscar Habitaciones
        </Button>
      </Form>
      {error && <Alert variant="danger">{error}</Alert>}
      <Row>
        {hotels.length > 0 ? (
          hotels.map(hotel => (
            <Col key={hotel.id_hotel} md={4} className="mb-4">
              <Card>
                <Carousel>
                  {(hotelImages[hotel.id_hotel] || []).map((img, index) => (
                    <Carousel.Item key={index}>
                      <img className="d-block w-100" src={img.url || defaultRoomImage} alt={`Slide ${index}`} />
                    </Carousel.Item>
                  ))}
                </Carousel>
                <Card.Body>
                  <Card.Title>{hotel.nombre}</Card.Title>
                  <Card.Text>
                    {hotel.descripcion}
                    <br />
                    {hotel.direccion}
                  </Card.Text>
                  <Button variant="primary" onClick={() => navigate(`/hoteles/${hotel.id}`)}>Ver Detalles</Button>
                </Card.Body>
              </Card>
            </Col>
          ))
        ) : (
          <Col>
            <p>No se encontraron hoteles con los criterios especificados.</p>
          </Col>
        )}
      </Row>
    </Container>
  );
};

export default HomePage;
