import React, { useState, useEffect} from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const EditReservationPage = ({ show, handleClose, reserva, actualizarReserva }) => {
    // State for input fields
    const [checkIn, setCheckIn] = useState(reserva.fechaIngreso);
    const [checkOut, setCheckOut] = useState(reserva.fechaSalida);
    const [tipoHabitacion, setTipoHabitacion] = useState(reserva.tipoHabitacion.toString());
    const [tiposHabitacionDisponibles, setTiposHabitacionDisponibles] = useState([]);

    const nombresTiposHabitacion = {
        1: "Doble",
        2: "Junior Suite",
        4: "Gran Suite",
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const reservaActualizada = {
            ...reserva,
            fechaIngreso: checkIn,
            fechaSalida: checkOut,
            tipoHabitacion: Number(tipoHabitacion),
        };
        try {
            await actualizarReserva(reservaActualizada);
            handleClose();
        } catch (error) {
            console.error('Error al actualizar la reserva:', error);
        }
    };

    useEffect(() => {
        const fetchTiposHabitacion = async () => {
            if (show && reserva.idHotel) {
                try {
                    const response = await fetch(`http://localhost:8080/habitaciones?hotelId=${reserva.idHotel}`);
                    if (!response.ok) throw new Error('Error al obtener los tipos de habitaci贸n');
                    const datosHabitaciones = await response.json();
                    const tiposUnicos = [...new Set(datosHabitaciones.map(hab => hab.tipo_habitacion))];
                    setTiposHabitacionDisponibles(tiposUnicos.map(tipo => ({
                        id: tipo,
                        nombre: nombresTiposHabitacion[tipo]
                    })).filter(tipo => tipo.nombre)); // Filtra por nombres definidos para evitar tipos no reconocidos
                } catch (error) {
                    console.error('Error al obtener los tipos de habitaci贸n:', error);
                }
            }
        };
        fetchTiposHabitacion();
    }, [show, reserva.idHotel]);




    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Editar Reserva</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="formCheckIn">
                        <Form.Label>Check-in</Form.Label>
                        <Form.Control
                            type="date"
                            value={checkIn}
                            onChange={(e) => setCheckIn(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formCheckOut">
                        <Form.Label>Check-out</Form.Label>
                        <Form.Control
                            type="date"
                            value={checkOut}
                            onChange={(e) => setCheckOut(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formTipoHabitacion">
                        <Form.Label>Tipo de Habitaci贸n</Form.Label>
                        <Form.Control as="select" value={tipoHabitacion} onChange={(e) => setTipoHabitacion(e.target.value)}>
                            {tiposHabitacionDisponibles.map(tipo => (
                                <option key={tipo.id} value={tipo.id}>{tipo.nombre}</option>
                            ))}
                        </Form.Control>
                    </Form.Group>
                    <Button variant="primary" type="submit">Guardar Cambios</Button>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>Cerrar</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default EditReservationPage;
