import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Alert } from 'react-bootstrap';

const HotelSearchForm = ({ onSearch }) => {
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [numeroHuespedes, setNumeroHuespedes] = useState(1);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPaises = async () => {
      try {
        const response = await fetch('http://localhost:8080/hoteles/pais');
        if (!response.ok) throw new Error('Error al cargar los paises');
        const data = await response.json();
        setPaises(data);
      } catch (error) {
        setError('Error al cargar los paises: ' + error.message);
      }
    };
    fetchPaises();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    onSearch(paisSeleccionado, numeroHuespedes);
  };






  return (
    <Container>
      <Row>
        <Col>
          <h2>Buscar Hoteles Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="pais">
              <Form.Label>País</Form.Label>
              <Form.Control as="select" value={paisSeleccionado} onChange={(e) => setPaisSeleccionado(e.target.value)}>
                <option value="">Seleccione un país</option>
                {paises.map((pais, index) => (
                  <option key={index} value={pais}>{pais}</option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="numeroHuespedes">
              <Form.Label>Número de Huéspedes</Form.Label>
              <Form.Control type="number" value={numeroHuespedes} onChange={(e) => setNumeroHuespedes(e.target.value)} />
            </Form.Group>
            <Button variant="primary" type="submit">
              Buscar
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default HotelSearchForm;
