import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Alert } from 'react-bootstrap';

const HotelSearchForm = ({ onSearch }) => {
  const [pais, setPais] = useState('');
  const [capacidadPersonas, setCapacidadPersonas] = useState(1);
  const [error, setError] = useState('');

  const buscarHoteles = async (e) => {
    e.preventDefault();
    console.log(`Buscando hoteles con país: ${pais}, capacidad de personas: ${capacidadPersonas}`);
    onSearch({ pais, capacidadPersonas });
  };

  return (
    <Container>
      <Row>
        <Col>
          <h2>Buscar Hoteles y Habitaciones Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={buscarHoteles}>
            <Form.Group>
              <Form.Label>País</Form.Label>
              <Form.Control type="text" value={pais} onChange={(e) => setPais(e.target.value)} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Número de Huéspedes</Form.Label>
              <Form.Control type="number" value={capacidadPersonas} onChange={(e) => setCapacidadPersonas(e.target.value)} />
            </Form.Group>
            <Button type="submit">Buscar</Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default HotelSearchForm;
