import React, { useState, useEffect } from 'react';
import { Container, Typography, Grid, TextField, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Paper } from '@mui/material';

function FAQs() {
    const [faqs, setFaqs] = useState([]);
    const [seccionesGenerales, setSeccionesGenerales] = useState([]);
    const [faqEdit, setFaqEdit] = useState({ pregunta: '', respuesta: '', id: null });
    const [seccionEdit, setSeccionEdit] = useState({ titulo: '', contenido: '', id: null });

    useEffect(() => {
        fetchFaqs();
        fetchSeccionesGenerales();
    }, []);

    // Fetch FAQs
    async function fetchFaqs() {
        const response = await fetch('http://localhost:8080/faqs');
        const data = await response.json();
        setFaqs(data);
    }

    // Fetch Secciones Generales
    async function fetchSeccionesGenerales() {
        const response = await fetch('http://localhost:8080/secciones-generales');
        const data = await response.json();
        setSeccionesGenerales(data);
    }

    // Handle change for FAQs and Secciones Generales
    function handleChange(e, setFunction) {
        const { name, value } = e.target;
        setFunction(prevState => ({ ...prevState, [name]: value }));
    }

    // Submit FAQ
    async function handleSubmitFaq(e) {
        e.preventDefault();
        const url = faqEdit.id ? `http://localhost:8080/faqs/${faqEdit.id}` : 'http://localhost:8080/faqs';
        const method = faqEdit.id ? 'PUT' : 'POST';

        await fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(faqEdit),
        });

        setFaqEdit({ pregunta: '', respuesta: '', id: null }); // Reset form
        fetchFaqs(); // Refetch FAQs
    }

    // Submit Sección General
    async function handleSubmitSeccion(e) {
        e.preventDefault();
        const url = seccionEdit.id ? `http://localhost:8080/secciones-generales/${seccionEdit.id}` : 'http://localhost:8080/secciones-generales';
        const method = seccionEdit.id ? 'PUT' : 'POST';

        await fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(seccionEdit),
        });

        setSeccionEdit({ titulo: '', contenido: '', id: null }); // Reset form
        fetchSeccionesGenerales(); // Refetch Secciones Generales
    }

    // Delete FAQ
    async function deleteFaq(id) {
        await fetch(`http://localhost:8080/faqs/${id}`, { method: 'DELETE' });
        fetchFaqs(); // Refetch FAQs
    }

    // Delete Sección General
    async function deleteSeccion(id) {
        await fetch(`http://localhost:8080/secciones-generales/${id}`, { method: 'DELETE' });
        fetchSeccionesGenerales(); // Refetch Secciones Generales
    }

    return (
      <Container maxWidth="md">
        <Typography variant="h4" component="h1" gutterBottom>FAQs y Secciones Generales</Typography>

        <Paper elevation={3} sx={{ p: 2, mb: 4 }}>
          <Typography variant="h6" component="h2">FAQs</Typography>
          {faqs.map(faq => (
            <Grid container spacing={2} key={faq.id}>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Pregunta"
                  value={faqEdit.id === faq.id ? faqEdit.pregunta : faq.pregunta}
                  onChange={(e) => setFaqEdit({ ...faq, pregunta: e.target.value })}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Respuesta"
                  value={faqEdit.id === faq.id ? faqEdit.respuesta : faq.respuesta}
                  onChange={(e) => setFaqEdit({ ...faq, respuesta: e.target.value })}
                />
              </Grid>
              <Grid item>
                <Button variant="contained" color="primary" onClick={() => handleOpenDialog(() => deleteFaq(faq.id))}>Eliminar</Button>
              </Grid>
            </Grid>
          ))}
          <Button variant="outlined" onClick={handleSubmitFaq}>Guardar Cambios</Button>
        </Paper>

        <Paper elevation={3} sx={{ p: 2 }}>
          <Typography variant="h6" component="h2">Secciones Generales</Typography>
          {/* Secciones Generales siguen un patrón similar al de FAQs */}
        </Paper>

        <Dialog open={openDialog} onClose={handleCloseDialog}>
          <DialogTitle>Confirmar eliminación</DialogTitle>
          <DialogContent>
            <DialogContentText>¿Estás seguro de que quieres eliminar este elemento?</DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>Cancelar</Button>
            <Button onClick={() => {deleteFunction(); handleCloseDialog();}} autoFocus>Eliminar</Button>
          </DialogActions>
        </Dialog>
      </Container>
    );
  }

  export default FAQs;
