import React, { useState, useEffect } from 'react';

function AdministrarUsuarios() {
  const [usuarios, setUsuarios] = useState([]);
  const [cargando, setCargando] = useState(false);
  const [error, setError] = useState('');
  const [usuarioForm, setUsuarioForm] = useState({
    rol: '',
    email: '',
    password: '',
    primerNombre: '',
    segundoNombre: '',
    primerApellido: '',
    segundoApellido: '',
    fechaNacimiento: '',
    nacionalidad: '',
    pasaporte: ''
  });

  useEffect(() => {
    fetchUsuarios();
  }, []);

  const fetchUsuarios = async () => {
    setCargando(true);
    try {
      const response = await fetch('http://localhost:8080/usuarios/detalles');
      if (!response.ok) {
        throw new Error('Algo salió mal al obtener los usuarios');
      }
      const data = await response.json();
      setUsuarios(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setCargando(false);
    }
  };

  const handleChange = (e) => {
    setUsuarioForm({ ...usuarioForm, [e.target.name]: e.target.value });
  };

  const crearUsuario = async () => {
    try {
      const response = await fetch('http://localhost:8080/usuarios/crear', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(usuarioForm),
      });

      if (!response.ok) {
        throw new Error('Error al crear el usuario');
      }

      fetchUsuarios(); // Actualizar la lista de usuarios
    } catch (error) {
      setError(error.message);
    }
  };

  if (cargando) return <p>Cargando usuarios...</p>;
  if (error) return <p>Hubo un error al cargar los usuarios: {error}</p>;

  return (
    <div>
      <h2>Administrar Usuarios</h2>
      <div>
        {/* Formulario para crear un nuevo usuario */}
        <h3>Crear nuevo usuario</h3>
        <input
          name="email"
          value={usuarioForm.email}
          onChange={handleChange}
          placeholder="Email"
        />
        {/* Agrega inputs para los otros campos siguiendo el ejemplo anterior */}
        <button onClick={crearUsuario}>Crear Usuario</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Email</th>
            <th>Rol</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.map((usuario) => (
            <tr key={usuario.id}>
              <td>{usuario.id}</td>
              <td>{usuario.primerNombre} {usuario.primerApellido}</td>
              <td>{usuario.email}</td>
              <td>{usuario.rolNombre}</td>
              <td>
                {/* Botones o enlaces para editar y eliminar usuarios */}
                <button onClick={() => {}}>Editar</button>
                <button onClick={() => {}}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdministrarUsuarios;
