import React, { useState, useEffect, useRef } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';


const HotelSearchForm = () => {
  const [destinos, setDestinos] = useState([]);
  const [selectedDestino, setSelectedDestino] = useState('');
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const [guests, setGuests] = useState(1);

  useEffect(() => {
    // Fetch de los países disponibles en hoteles
    const fetchPaises = async () => {
      try {
        const response = await fetch('http://localhost:8080/hoteles/paises');
        const data = await response.json();
        setDestinos(data);
      } catch (error) {
        console.error('Error fetching paises:', error);
      }
    };

    fetchPaises();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Asumiendo que tienes un endpoint que soporte estos criterios de búsqueda
    try {
      const response = await fetch(`http://localhost:8080/habitaciones/disponibles?pais=${selectedDestino}&checkIn=${checkInDate.toISOString().split('T')[0]}&checkOut=${checkOutDate.toISOString().split('T')[0]}&guests=${guests}`);
      if (!response.ok) throw new Error('Error fetching available rooms');
      const availableRooms = await response.json();
      // Procesa las habitaciones disponibles como necesites
      console.log(availableRooms);
    } catch (error) {
      console.error('Error fetching available rooms:', error);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Row>
        <Col>
          <Form.Group controlId="destinoSelect">
            <Form.Label>Destino</Form.Label>
            <Form.Control as="select" value={selectedDestino} onChange={(e) => setSelectedDestino(e.target.value)}>
              <option value="">Seleccione un destino</option>
              {destinos.map((destino) => (
                <option key={destino} value={destino}>{destino}</option>
              ))}
            </Form.Control>
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="checkInDate">
            <Form.Label>Check-in</Form.Label>
            <DatePicker selected={checkInDate} onChange={date => setCheckInDate(date)} dateFormat="yyyy-MM-dd" />
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="checkOutDate">
            <Form.Label>Check-out</Form.Label>
            <DatePicker selected={checkOutDate} onChange={date => setCheckOutDate(date)} dateFormat="yyyy-MM-dd" />
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="guestsNumber">
            <Form.Label>Número de Huéspedes</Form.Label>
            <Form.Control type="number" value={guests} onChange={(e) => setGuests(e.target.value)} min="1" />
          </Form.Group>
        </Col>
      </Row>
      <Button type="submit" className="mt-3">Buscar Disponibilidad</Button>
    </Form>
  );
};

export default HotelSearchForm;
