import React, { useState, useEffect } from 'react';

function AdministrarUsuarios() {
  const [usuarios, setUsuarios] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUsuarios();
  }, []);

  const fetchUsuarios = async () => {
    try {
      const response = await fetch('http://localhost:8080/usuarios/detalles');
      if (!response.ok) throw new Error('Algo salió mal al obtener los usuarios');
      const data = await response.json();
      setUsuarios(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setCargando(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const usuarioData = Object.fromEntries(formData.entries());

    try {
      const response = await fetch('http://localhost:8080/usuarios/crear', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(usuarioData),
      });

      if (!response.ok) throw new Error('Error al crear el usuario');
      fetchUsuarios(); // Refresh the list of users after adding a new one
    } catch (error) {
      setError(error.message);
    }
  };

  if (cargando) return <p>Cargando usuarios...</p>;
  if (error) return <p>Hubo un error al cargar los usuarios: {error}</p>;

  return (
    <div>
      <h2>Administrar Usuarios</h2>
      <form onSubmit={handleSubmit}>
        <input name="email" placeholder="Email" required />
        <input name="password" type="password" placeholder="Contraseña" required />
        {/* Añade aquí los inputs para los otros campos */}
        <button type="submit">Crear Usuario</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Email</th>
            <th>Rol</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.map(usuario => (
            <tr key={usuario.id}>
              <td>{usuario.id}</td>
              <td>{usuario.primerNombre} {usuario.primerApellido}</td>
              <td>{usuario.email}</td>
              <td>{usuario.rolNombre}</td>
              <td>
                {/* Placeholder for edit and delete actions */}
                <button>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdministrarUsuarios;
