import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const tiposHabitacionMap = {
  "Doble": "1",
  "Junior Suite": "2",
  "Suite": "3",
  "Gran Suite": "4",
};

const EditReservationPage = ({ show, handleClose, reserva, actualizarReserva }) => {
  console.log('Reserva actual en modal:', reserva); // Verificación temporal

  const [checkIn, setCheckIn] = useState(reserva.fechaIngreso);
  const [checkOut, setCheckOut] = useState(reserva.fechaSalida);

  // Invierte el mapeo para poder inicializar correctamente desde un número
  const tiposHabitacionInvertido = Object.keys(tiposHabitacionMap).reduce((acc, key) => {
    const value = tiposHabitacionMap[key];
    acc[value] = key;
    return acc;
  }, {});

  const [tipoHabitacion, setTipoHabitacion] = useState(() => {
    return tiposHabitacionMap[reserva.tipoHabitacion] || "1"; // Asumiendo que reserva.tipoHabitacion es un string
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const reservaActualizada = {
        ...reserva,
        fechaIngreso: checkIn,
        fechaSalida: checkOut,
        tipoHabitacion: parseInt(tipoHabitacion, 10), // Asegurarse de enviar un entero
    };

    console.log('Enviando reserva actualizada:', reservaActualizada);

    try {
        const response = await fetch(`http://localhost:8080/reservas/${reserva.idReserva}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(reservaActualizada),
        });

        if (!response.ok) {
            throw new Error(`No se pudo actualizar la reserva, status: ${response.status}`);
        }

        console.log('Reserva actualizada con éxito');
        handleClose(); // Cerrar modal si la actualización es exitosa
        actualizarReserva(); // Notificar al componente padre para que actualice la lista de reservas
    } catch (error) {
        console.error('Error al actualizar la reserva:', error);
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar Reserva</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          {/* Campos de fecha */}
          <Form.Group controlId="formCheckIn">
            <Form.Label>Check-in</Form.Label>
            <Form.Control
              type="date"
              value={checkIn}
              onChange={(e) => setCheckIn(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formCheckOut">
            <Form.Label>Check-out</Form.Label>
            <Form.Control
              type="date"
              value={checkOut}
              onChange={(e) => setCheckOut(e.target.value)}
            />
          </Form.Group>
          {/* Selector de tipo de habitación */}
          <Form.Group controlId="formTipoHabitacion">
            <Form.Label>Tipo de Habitación</Form.Label>
            <Form.Control
              as="select"
              value={tipoHabitacion}
              onChange={(e) => setTipoHabitacion(e.target.value)}
            >
              <option value="1">Doble</option>
              <option value="2">Junior Suite</option>
              <option value="3">Suite</option>
              <option value="4">Gran Suite</option>
            </Form.Control>
          </Form.Group>
          <Button variant="primary" type="submit">
            Guardar Cambios
          </Button>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cerrar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditReservationPage;
