import React, { useState, useEffect } from 'react';
import { Button, Form, Card, Container, Row } from 'react-bootstrap';
import { useUser } from './UserContext';

function Comentarios({ idHabitacion }) {
  const [comentarios, setComentarios] = useState([]);
  const [textoComentario, setTextoComentario] = useState('');
  const [rating, setRating] = useState(1);
  const [idComentarioPadre, setIdComentarioPadre] = useState(null);
  const { user } = useUser();

  useEffect(() => {
    fetchComentarios();
  }, [idHabitacion]);

  const fetchComentarios = async () => {
    try {
      const response = await fetch(`http://localhost:8080/comentarios/por-habitacion/${idHabitacion}`);
      const data = await response.json();
      setComentarios(data);
    } catch (error) {
      console.error('Error al recuperar los comentarios', error);
      setComentarios([]);
    }
  };

  const crearComentario = async (e) => {
    e.preventDefault();
    if (!user || !user.id) {
      alert('Debes estar registrado y haber iniciado sesión para realizar un comentario.');
      return;
    }

    const comentario = {
      idHabitacion,
      textoComentario,
      rating,
      idComentarioPadre,
      idUsuario: user.id,
    };

    try {
      const response = await fetch('http://localhost:8080/comentarios', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(comentario),
      });

      if (response.ok) {
        setTextoComentario('');
        setRating(1);
        setIdComentarioPadre(null);
        fetchComentarios();
      }
    } catch (error) {
      console.error('Error al crear el comentario', error);
    }
  };

  // Función para manejar la respuesta a un comentario
  const responderAComentario = (idComentario) => {
    setIdComentarioPadre(idComentario);
    window.scrollTo({ top: 0, behavior: 'smooth' }); // Opcional: Mueve el scroll al formulario
  };

  // Función para renderizar los comentarios de forma recursiva
  const renderComentarios = (comentariosList, padding = 0) => {
    return comentariosList.map((comentario) => (
      <React.Fragment key={comentario.idComentario}>
        <Card className="mb-3" style={{ marginLeft: `${padding}px` }}>
          <Card.Body>
            <Card.Title>Comentario de {comentario.nombreUsuario} - Rating: {comentario.rating}</Card.Title>
            <Card.Text>{comentario.textoComentario}</Card.Text>
            <Button variant="link" onClick={() => responderAComentario(comentario.idComentario)}>Responder</Button>
            <Card.Footer>Comentado el: {new Date(comentario.fechaComentario).toLocaleString()}</Card.Footer>
          </Card.Body>
        </Card>
        {comentario.respuestas && renderComentarios(comentario.respuestas, padding + 20)}
      </React.Fragment>
    ));
  };

  return (
    <Container>
      <Row>
        <Form onSubmit={crearComentario}>
          <Form.Group className="mb-3" controlId="formComentarioTexto">
            <Form.Label>Comentario</Form.Label>
            <Form.Control
              type="text"
              placeholder="Escribe tu comentario aquí"
              value={textoComentario}
              onChange={(e) => setTextoComentario(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formComentarioRating">
            <Form.Label>Rating</Form.Label>
            <Form.Control
              as="select"
              value={rating}
              onChange={(e) => setRating(e.target.value)}
              required
            >
              {[1, 2, 3, 4, 5].map((value) => (
                <option key={value} value={value}>{value}</option>
              ))}
            </Form.Control>
          </Form.Group>
          {idComentarioPadre && (
            <p>Respondiendo al comentario #{idComentarioPadre} <Button variant="secondary" onClick={() => setIdComentarioPadre(null)}>Cancelar</Button></p>
          )}
          <Button variant="primary" type="submit">Enviar Comentario</Button>
        </Form>
      </Row>
      <Row>
        {renderComentarios(comentarios)}
      </Row>
    </Container>
  );
}

export default Comentarios;
