import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import HotelSearchForm from './HotelSearchForm';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };

  useEffect(() => {
    // Inicialmente carga todos los hoteles
    fetchHotelsAndRooms();
  }, []);

  const fetchHotelsAndRooms = async (pais = '', fechaInicio = '', fechaFin = '', numeroHuéspedes = '') => {
    try {
      // Determina si se están aplicando filtros
      const aplicandoFiltros = pais || fechaInicio || fechaFin || numeroHuéspedes;

      // Construye la URL basada en si se aplican filtros o no
      let url = aplicandoFiltros
        ? `http://localhost:8080/hoteles/buscar?pais=${encodeURIComponent(pais)}&fechaInicio=${fechaInicio}&fechaFin=${fechaFin}&huéspedes=${numeroHuéspedes}`
        : 'http://localhost:8080/hoteles'; // Usa un endpoint diferente o el mismo si tu backend maneja esto en el mismo endpoint

      console.log(`Filtrando hoteles con URL: ${url}`); // Log para depuración

      const hotelsResponse = await fetch(url);
      if (!hotelsResponse.ok) throw new Error('Error al cargar hoteles');
      const hotelsData = await hotelsResponse.json();

      setHotels(hotelsData);
      console.log('Hoteles cargados:', hotelsData); // Log para depuración
    } catch (error) {
      console.error('Error al cargar hoteles:', error);
      setHotels([]);
    }
  };


  const handleSearch = (searchParams) => {
    console.log('Aplicando búsqueda con:', searchParams); // Log para depuración
    fetchHotelsAndRooms(searchParams.pais, searchParams.fechaInicio, searchParams.fechaFin, searchParams.numeroHuéspedes);
  };

  return (
    <Container className="my-5">
      <HotelSearchForm onSearch={handleSearch} />
      {hotels.length > 0 ? (
        hotels.map((hotel) => (
          <React.Fragment key={hotel.id_hotel}>
            <Row>
              <Col md={12}>
                <h2>Hotel: {hotel.nombre}</h2>
                <p>{hotel.ciudad}, {hotel.pais}</p>
                <p>Dirección: {hotel.direccion}</p>
              </Col>
            </Row>
            <Row>
              {hotel.rooms.map((room) => (
                <Col key={room.id_habitacion} md={4}>
                  <Card className="mb-3">
                    <Card.Body>
                      <Card.Title>Habitación: {tiposHabitacion[room.tipo_habitacion]}</Card.Title>
                      <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                      <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                      <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                      <Card.Text>Valoración: {room.valuacion} estrellas</Card.Text>
                      <Card.Img variant="top" src={defaultRoomImage} />
                      <Button variant="primary" onClick={() => {
                        navigate('/checkout', {
                          state: {
                            hotelDetails: { ...hotel },
                            roomDetails: {
                              ...room,
                              idHabitacion: room.id_habitacion,
                              roomType: tiposHabitacion[room.tipo_habitacion],
                              roomPrice: room.precioxnoche,
                              capacidadPersonas: room.capacidad_personas,
                            },
                          },
                        });
                      }}>
                        Reservar
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>
          </React.Fragment>
        ))
      ) : (
        <p>No se encontraron hoteles para los criterios de búsqueda.</p>
      )}
    </Container>
  );
};

export default HomePage;
