import React, { useState, useEffect } from 'react';

function FAQS() {
    const [faqs, setFaqs] = useState([]);
    const [seccionesGenerales, setSeccionesGenerales] = useState([]);
    const [faq, setFaq] = useState({ pregunta: '', respuesta: '' });
    const [seccion, setSeccion] = useState({ titulo: '', contenido: '' });
    const [isEditingFaq, setIsEditingFaq] = useState(null);
    const [isEditingSeccion, setIsEditingSeccion] = useState(null);

    useEffect(() => {
        fetchFaqs();
        fetchSeccionesGenerales();
    }, []);

    async function fetchFaqs() {
        const res = await fetch('http://localhost:8080/faqs');
        const data = await res.json();
        setFaqs(data);
    }

    async function fetchSeccionesGenerales() {
        const res = await fetch('http://localhost:8080/secciones-generales');
        const data = await res.json();
        setSeccionesGenerales(data);
    }

    const handleChangeFaq = (e) => {
        const { name, value } = e.target;
        setFaq(prev => ({ ...prev, [name]: value }));
    };

    const handleChangeSeccion = (e) => {
        const { name, value } = e.target;
        setSeccion(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmitFaq = async (e) => {
        e.preventDefault();
        const method = isEditingFaq ? 'PUT' : 'POST';
        const url = isEditingFaq ? `http://localhost:8080/faqs/${isEditingFaq}` : 'http://localhost:8080/faqs';

        await fetch(url, {
            method,
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(faq),
        });

        setFaq({ pregunta: '', respuesta: '' });
        setIsEditingFaq(null);
        fetchFaqs();
    };

    const handleSubmitSeccion = async (e) => {
        e.preventDefault();
        const method = isEditingSeccion ? 'PUT' : 'POST';
        const url = isEditingSeccion ? `http://localhost:8080/secciones-generales/${isEditingSeccion}` : 'http://localhost:8080/secciones-generales';

        await fetch(url, {
            method,
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(seccion),
        });

        setSeccion({ titulo: '', contenido: '' });
        setIsEditingSeccion(null);
        fetchSeccionesGenerales();
    };

    const editFaq = (faq) => {
        setFaq({ pregunta: faq.pregunta, respuesta: faq.respuesta });
        setIsEditingFaq(faq.id);
    };

    const deleteFaq = async (id) => {
        await fetch(`http://localhost:8080/faqs/${id}`, { method: 'DELETE' });
        fetchFaqs();
    };

    const editSeccion = (seccion) => {
        setSeccion({ titulo: seccion.titulo, contenido: seccion.contenido });
        setIsEditingSeccion(seccion.id);
    };

    const deleteSeccion = async (id) => {
        await fetch(`http://localhost:8080/secciones-generales/${id}`, { method: 'DELETE' });
        fetchSeccionesGenerales();
    };

    return (
        <div>
            <h2>FAQs</h2>
            {faqs.map((faq) => (
                <div key={faq.id}>
                    <p>Pregunta: {faq.pregunta}</p>
                    <p>Respuesta: {faq.respuesta}</p>
                    <button onClick={() => editFaq(faq)}>Editar</button>
                    <button onClick={() => deleteFaq(faq.id)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={handleSubmitFaq}>
                <input name="pregunta" value={faq.pregunta} onChange={handleChangeFaq} placeholder="Pregunta" required />
                <input name="respuesta" value={faq.respuesta} onChange={handleChangeFaq} placeholder="Respuesta" required />
                <button type="submit">Guardar FAQ</button>
            </form>

            <h2>Secciones Generales</h2>
            {seccionesGenerales.map((seccion) => (
                <div key={seccion.id}>
                    <p>Título: {seccion.titulo}</p>
                    <p>Contenido: {seccion.contenido}</p>
                    <button onClick={() => editSeccion(seccion)}>Editar</button>
                    <button onClick={() => deleteSeccion(seccion.id)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={handleSubmitSeccion}>
                <input name="titulo" value={seccion.titulo} onChange={handleChangeSeccion} placeholder="Título" required />
                <textarea name="contenido" value={seccion.contenido} onChange={handleChangeSeccion} placeholder="Contenido" required />
                <button type="submit">Guardar Sección</button>
            </form>
        </div>
    );
}

export default FAQS;
