import React, { useState, useEffect } from 'react';
import { Button, Form, Card, Container, Row } from 'react-bootstrap';

function Comentarios({ idHabitacion }) {
  const [comentarios, setComentarios] = useState([]);
  const [textoComentario, setTextoComentario] = useState('');
  const [rating, setRating] = useState(1);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchComentarios();
  }, [idHabitacion]);

  const fetchComentarios = async () => {
    try {
      const response = await fetch(`http://localhost:8080/comentarios/por-habitacion/${idHabitacion}`);
      if (!response.ok) {
        throw new Error('Falló la solicitud al servidor');
      }
      const data = await response.json();
      if (Array.isArray(data)) { // Verifica si data es un arreglo
        setComentarios(data);
      } else {
        throw new Error('Formato de respuesta inesperado');
      }
    } catch (error) {
      console.error("Error al cargar los comentarios:", error);
      setError("Error al cargar los comentarios.");
    }
  };

  const crearComentario = async (e) => {
    e.preventDefault();
    try {
      const comentario = {
        idHabitacion, 
        textoComentario, 
        rating,
        // Asegúrate de proporcionar un idUsuario válido
        idUsuario: "1001" // Ejemplo de idUsuario hardcodeado
      };
      const response = await fetch('http://localhost:8080/comentarios', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(comentario),
      });

      if (!response.ok) {
        throw new Error('Falló al enviar el comentario');
      }

      setTextoComentario('');
      setRating(1);
      fetchComentarios();
    } catch (error) {
      console.error("Error al crear el comentario:", error);
      setError("Error al crear el comentario.");
    }
  };

  return (
    <Container>
      <Row>
        <Form onSubmit={crearComentario}>
          {/* Formulario para nuevo comentario */}
        </Form>
      </Row>
      <Row>
        {comentarios.length > 0 ? (
          comentarios.map((comentario) => (
            <Card key={comentario.idComentario}>
              {/* Datos del comentario */}
            </Card>
          ))
        ) : (
          <p>No hay comentarios aún.</p>
        )}
      </Row>
      {error && <p>{error}</p>}
    </Container>
  );
}

export default Comentarios;
