import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';

const TipoHabitacionImagenesEditor = ({ idTipoHabitacion }) => {
    const [imagenes, setImagenes] = useState([]);
    const [nuevaImagen, setNuevaImagen] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        const cargarImagenes = async () => {
            try {
                const response = await fetch(`http://localhost:8080/tipos_habitacion/${idTipoHabitacion}`);
                if (!response.ok) throw new Error('Error al cargar imágenes');
                const data = await response.json();
                // Verificar que la respuesta es un arreglo antes de intentar actualizar el estado
                if (Array.isArray(data)) {
                    setImagenes(data);
                } else {
                    setError('Formato de datos incorrecto: se esperaba un arreglo');
                }
            } catch (error) {
                setError('Error al cargar imágenes: ' + error.message);
            }
        };

        if (idTipoHabitacion) cargarImagenes();
    }, [idTipoHabitacion]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!nuevaImagen) return;

        try {
            const updatedImages = [...imagenes, nuevaImagen];
            await fetch(`http://localhost:8080/tipos_habitacion/${idTipoHabitacion}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updatedImages),
            });

            setImagenes(updatedImages);
            setNuevaImagen('');
        } catch (error) {
            setError('Error al actualizar imágenes: ' + error.message);
        }
    };

    const handleEliminarImagen = async (index) => {
        const updatedImages = imagenes.filter((_, i) => i !== index);
        try {
            await fetch(`http://localhost:8080/tipos_habitacion/${idTipoHabitacion}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updatedImages),
            });

            setImagenes(updatedImages);
        } catch (error) {
            setError('Error al eliminar imagen: ' + error.message);
        }
    };

    return (
        <div>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form onSubmit={handleSubmit}>
                <Form.Group>
                    <Form.Label>Añadir nueva imagen</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="URL de la imagen"
                        value={nuevaImagen}
                        onChange={e => setNuevaImagen(e.target.value)}
                    />
                </Form.Group>
                <Button type="submit">Añadir imagen</Button>
            </Form>
            <div>
                {imagenes.map((imagen, index) => (
                    <div key={index} style={{ marginTop: '10px' }}>
                        <img src={imagen} alt="Imagen de tipo de habitación" style={{ width: '100px', height: '100px', marginRight: '10px' }} />
                        <Button variant="danger" onClick={() => handleEliminarImagen(index)}>Eliminar</Button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TipoHabitacionImagenesEditor;
