import React, { useState, useEffect } from 'react';
import { Container, Form, Button, Row, Col } from 'react-bootstrap';

const HotelSearchForm = () => {
  const [destinos, setDestinos] = useState([]);
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [numeroHuéspedes, setNumeroHuéspedes] = useState('');

  useEffect(() => {
    // Asumiendo que tienes una función para cargar los países de tus hoteles
    cargarDestinos();
  }, []);

  const cargarDestinos = async () => {
    // Lógica para cargar los destinos (países) desde tu API
    // Esto es un ejemplo y deberías reemplazarlo con tu propia lógica de carga
    const respuesta = await fetch('http://localhost:8080/hoteles/paises');
    const paises = await respuesta.json();
    setDestinos(paises);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica para manejar la búsqueda con las fechas y número de huéspedes
  };

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Row className="mb-3">
          <Col>
            <Form.Group controlId="formDestino">
              <Form.Label>Destino</Form.Label>
              <Form.Control as="select" value={destinoSeleccionado} onChange={(e) => setDestinoSeleccionado(e.target.value)}>
                <option>Selecciona un destino</option>
                {destinos.map(destino => (
                  <option key={destino.id} value={destino.pais}>{destino.pais}</option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formFechaInicio">
              <Form.Label>Check-in</Form.Label>
              <Form.Control type="date" value={fechaInicio} onChange={(e) => setFechaInicio(e.target.value)} />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formFechaFin">
              <Form.Label>Check-out</Form.Label>
              <Form.Control type="date" value={fechaFin} onChange={(e) => setFechaFin(e.target.value)} />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formNumeroHuéspedes">
              <Form.Label>Número de Huéspedes</Form.Label>
              <Form.Control type="number" value={numeroHuéspedes} onChange={(e) => setNumeroHuéspedes(e.target.value)} />
            </Form.Group>
          </Col>
        </Row>
        <Button variant="primary" type="submit">
          Buscar
        </Button>
      </Form>
    </Container>
  );
};

export default HotelSearchForm;
