import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import Comentarios from './Comentarios';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [fechaIngreso, setFechaIngreso] = useState('');
  const [fechaSalida, setFechaSalida] = useState('');
  const [numeroPersonas, setNumeroPersonas] = useState('');
  const [habitaciones, setHabitaciones] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [hotelImages, setHotelImages] = useState({});

  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };

  useEffect(() => {
    fetchPaises();
    fetchHotelsAndRooms();
  }, []);

  const fetchHotelImages = async (idHotel) => {
    try {
      const response = await fetch(`http://localhost:8080/hoteles/${idHotel}/imagenes`);
      if (!response.ok) throw new Error('Error al cargar imágenes del hotel');
      const images = await response.json();
      setHotelImages(prevImages => ({ ...prevImages, [idHotel]: images }));
    } catch (error) {
      console.error('Error fetching hotel images:', error);
    }
  };

  useEffect(() => {
    hotels.forEach((hotel) => {
      fetchHotelImages(hotel.id_hotel);
    });
  }, [hotels]);

  const fetchPaises = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles/pais');
      if (!response.ok) throw new Error('Error al cargar los países');
      const data = await response.json();
      setPaises(data);
    } catch (error) {
      setError('Error al cargar los países: ' + error.message);
    }
  };

  const fetchHotelsAndRooms = async () => {
    let url = paisSeleccionado ? `http://localhost:8080/hoteles/por-pais/${paisSeleccionado}` : `http://localhost:8080/hoteles`;
    try {
      const hotelsResponse = await fetch(url);
      if (!hotelsResponse.ok) throw new Error('Error al cargar hoteles');
      const hotelsData = await hotelsResponse.json();
      setHotels(hotelsData);
    } catch (error) {
      setError('Error al cargar hoteles: ' + error.message);
    }
  };

  const fetchHabitaciones = async (e) => {
    e.preventDefault();
    if (!fechaIngreso || !fechaSalida) {
      setError('La fecha de ingreso y la fecha de salida son obligatorias.');
      return;
    }
    let url = `http://localhost:8080/habitaciones/buscar?fechaIngreso=${fechaIngreso}&fechaSalida=${fechaSalida}`;
    if (paisSeleccionado) url += `&pais=${paisSeleccionado}`;
    if (numeroPersonas) url += `&numeroPersonas=${numeroPersonas}`;
    try {
      const response = await fetch(url);
      const data = await response.json();
      setHabitaciones(data);
    } catch (error) {
      setError('Error al buscar habitaciones disponibles: ' + error.message);
    }
  };


  return (
    <Container className="my-5">
      <Row>
        <Col md={12}>
          <h2>Buscar Habitaciones Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={fetchHabitaciones}>
            <Form.Group controlId="pais">
              <Form.Label>País</Form.Label>
              <Form.Control as="select" value={paisSeleccionado} onChange={e => setPaisSeleccionado(e.target.value)}>
                <option value="">Seleccione un país</option>
                {paises.map((pais, index) => (
                  <option key={index} value={pais}>{pais}</option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="fechaIngreso">
              <Form.Label>Fecha de Ingreso</Form.Label>
              <Form.Control type="date" required value={fechaIngreso} onChange={e => setFechaIngreso(e.target.value)} />
            </Form.Group>

            <Form.Group controlId="fechaSalida">
              <Form.Label>Fecha de Salida</Form.Label>
              <Form.Control type="date" required value={fechaSalida} onChange={e => setFechaSalida(e.target.value)} />
            </Form.Group>

            <Form.Group controlId="numeroPersonas">
              <Form.Label>Número de Personas</Form.Label>
              <Form.Control as="select" value={numeroPersonas} onChange={e => setNumeroPersonas(e.target.value)}>
                <option value="">Cualquiera</option>
                {[...Array(10).keys()].map(n => (
                  <option key={n + 1} value={n + 1}>{n + 1}</option>
                ))}
              </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit">Buscar</Button>
          </Form>
        </Col>
      </Row>
      <Row>
        {hotels.length > 0 ? (
          hotels.map((hotel) => (
            <React.Fragment key={hotel.id_hotel}>
              <Col md={10} className="mt-4">
                <h3>Hotel: {hotel.nombre}</h3>
                <p>{hotel.ciudad}, {hotel.pais}</p>
                <p>Dirección: {hotel.direccion}</p>
                {hotelImages[hotel.id_hotel] && hotelImages[hotel.id_hotel].length > 0 && (
                  <Carousel interval={null}>
                    {hotelImages[hotel.id_hotel].map((image, index) => (
                      <Carousel.Item key={index}>
                        <img
                          className="d-block w-100"
                          src={image}
                          alt={`Imagen del hotel ${hotel.nombre}`}
                        />
                      </Carousel.Item>
                    ))}
                  </Carousel>
                )}
              </Col>
              {hotel.rooms && hotel.rooms.map((room) => (
                <Col key={room.id_habitacion} md={6}>
                  <Card className="mb-3">
                    <Card.Img variant="top" src={room.imagenUrl || defaultRoomImage} />
                    <Card.Body>
                      <Card.Title>Habitación: {tiposHabitacion[room.tipo_habitacion]}</Card.Title>
                      <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                      <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                      <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                      <Card.Text>Valoración: {room.valuacion} estrellas</Card.Text>
                      <Button variant="primary" onClick={() => navigate('/checkout', {
                        state: {
                          hotelDetails: { ...hotel },
                          roomDetails: {
                            ...room,
                            idHabitacion: room.id_habitacion,
                            roomType: tiposHabitacion[room.tipo_habitacion],
                            roomPrice: room.precioxnoche,
                            capacidadPersonas: room.capacidad_personas
                          }
                        }
                      })}>
                        Reservar
                      </Button>
                      <Comentarios idHabitacion={room.id_habitacion} />
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </React.Fragment>
          ))
        ) : (
          <Col>
            <p className="mt-4">No se encontraron hoteles. Por favor, intenta nuevamente con diferentes criterios de búsqueda.</p>
          </Col>
        )}
        {habitaciones.length > 0 ? (
          habitaciones.map((habitacion) => (
            <React.Fragment key={habitacion.id_habitacion}>
              <Col md={6} className="mt-4">
                <Card className="mb-3">
                  <Card.Img variant="top" src={habitacion.imagenUrl || defaultRoomImage} />
                  <Card.Body>
                    <Card.Title>Habitación: {tiposHabitacion[habitacion.tipo_habitacion]}</Card.Title>
                    <Card.Text>Número de habitación: {habitacion.numero_habitacion}</Card.Text>
                    <Card.Text>Capacidad máxima: {habitacion.capacidad_personas} personas</Card.Text>
                    <Card.Text>Precio por noche: ${habitacion.precioxnoche}</Card.Text>
                    <Card.Text>Valoración: {habitacion.valuacion} estrellas</Card.Text>
                    <Button variant="primary" onClick={() => navigate('/checkout', {
                      state: {
                        roomDetails: {
                          ...habitacion,
                          idHabitacion: habitacion.id_habitacion,
                          roomType: tiposHabitacion[habitacion.tipo_habitacion],
                          roomPrice: habitacion.precioxnoche,
                          capacidadPersonas: habitacion.capacidad_personas
                        }
                      }
                    })}>
                      Reservar
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            </React.Fragment>
          ))
        ) : (
          <Col>
            <p className="mt-4">No se encontraron habitaciones disponibles. Intenta nuevamente con diferentes criterios de búsqueda.</p>
          </Col>
        )}
      </Row>
    </Container>
  );

};

export default HomePage;
