import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Image, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import Comentarios from './Comentarios';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [fechaIngreso, setFechaIngreso] = useState('');
  const [fechaSalida, setFechaSalida] = useState('');
  const [numeroPersonas, setNumeroPersonas] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [hotelImages, setHotelImages] = useState({});
  const [roomTypes, setRoomTypes] = useState({});



  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };


  useEffect(() => {
    fetchRoomTypes();
    fetchPaises();
    fetchHotelsAndRooms(paisSeleccionado);
  }, []);







  //IMAGENES

  const fetchHotelImages = async (idHotel) => {
    try {
      const response = await fetch(`http://localhost:8080/hoteles/${idHotel}/imagenes`);
      if (!response.ok) throw new Error('Error al cargar imágenes del hotel');
      const images = await response.json();
      setHotelImages(prev => ({ ...prev, [idHotel]: images }));
    } catch (error) {
      console.error('Error fetching hotel images:', error);
    }
  };

  useEffect(() => {
    hotels.forEach((hotel) => {
      fetchHotelImages(hotel.id_hotel);
    });
  }, [hotels]);



  const fetchRoomTypes = async () => {
    try {
      const response = await fetch('http://localhost:8080/tipos_habitacion');
      if (!response.ok) throw new Error('Error al cargar los tipos de habitación');
      const data = await response.json();
      setRoomTypes(data.reduce((map, roomType) => {
        map[roomType.id_tipo] = roomType.imagenUrl || defaultRoomImage; 
        return map;
      }, {}));
    } catch (error) {
      setError('Error al cargar los tipos de habitación: ' + error.message);
    }
  };




  const fetchPaises = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles/pais');
      if (!response.ok) throw new Error('Error al cargar los países');
      const data = await response.json();
      setPaises(data);
    } catch (error) {
      setError('Error al cargar los países: ' + error.message);
    }
  };

  const fetchHotelsAndRooms = async () => {
    let url = `http://localhost:8080/habitaciones/buscar?pais=${paisSeleccionado}&fechaIngreso=${fechaIngreso}&fechaSalida=${fechaSalida}&numeroPersonas=${numeroPersonas}`;
    try {
      const response = await fetch(url);
      if (!response.ok) throw new Error('Error al cargar habitaciones');
      const data = await response.json();
      setHotels(data.hotels || []);
      const hotelIds = data.hotels.map(hotel => hotel.id_hotel);
      hotelIds.forEach(idHotel => fetchHotelImages(idHotel));
    } catch (error) {
      setError('Error al cargar habitaciones: ' + error.message);
    }
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetchHotelsAndRooms();
  };


  return (
    <Container className="my-5">
      <Row>
        <Col md={12}>
          <h2>Buscar Habitaciones Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridState">
                <Form.Label>País</Form.Label>
                <Form.Control as="select" defaultValue="Seleccione un país..." onChange={e => setPaisSeleccionado(e.target.value)}>
                  <option>Seleccione un país...</option>
                  {paises.map(pais => (
                    <option key={pais} value={pais}>{pais}</option>
                  ))}
                </Form.Control>
              </Form.Group>

              <Form.Group as={Col} controlId="formGridCapacity">
                <Form.Label>Capacidad de Personas</Form.Label>
                <Form.Control as="select" defaultValue="" onChange={e => setNumeroPersonas(e.target.value)}>
                  <option value="">Cualquiera</option>
                  {Array.from({ length: 10 }, (_, i) => (
                    <option key={i + 1} value={i + 1}>{i + 1}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Form.Row>

            <Button variant="primary" onClick={() => fetchHotelsAndRooms(paisSeleccionado, numeroPersonas)}>
              Buscar
            </Button>
          </Form>
        </Col>
      </Row>
      <Row className="mt-4">
        {hotels.length > 0 ? (
          hotels.map(hotel => (
            <React.Fragment key={hotel.id_hotel}>
              <Col md={6} className="mb-4">
                <Card>
                  <Card.Header as="h5">{hotel.nombre}</Card.Header>
                  <Carousel>
                    {hotelImages[hotel.id_hotel]?.map((image, index) => (
                      <Carousel.Item key={index}>
                        <img
                          className="d-block w-100"
                          src={image}
                          alt={`Slide ${index}`}
                        />
                      </Carousel.Item>
                    ))}
                  </Carousel>
                  <Card.Body>
                    <Card.Title>{hotel.ciudad}, {hotel.pais}</Card.Title>
                    <Card.Text>
                      Dirección: {hotel.direccion}
                    </Card.Text>
                    {hotel.rooms.map(room => (
                      <div key={room.id_habitacion} className="mb-2">
                        <h6>Habitación: {tiposHabitacion[room.tipo_habitacion]}</h6>
                        <img src={room.imagenUrl} alt="Habitación" style={{ width: '100%' }} />
                        <p>Capacidad: {room.capacidad_personas} personas - Precio: ${room.precioxnoche} por noche</p>
                      </div>
                    ))}
                  </Card.Body>
                </Card>
              </Col>



              {hotel.rooms && hotel.rooms.map((room) => (
                <Col key={room.id_habitacion} md={6}>
                  <Card className="mb-3">
                  <Card.Img variant="top" src={room.imagenUrl || defaultRoomImage} />
                    <Card.Body>
                    <Card.Title>Habitación: {tiposHabitacion[room.tipo_habitacion]}</Card.Title>
                      <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                      <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                      <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                      <Card.Text>Valoración: {room.valuacion} estrellas</Card.Text>
                      <Button variant="primary" onClick={() => {
                      console.log("Navigating with hotelDetails:", hotel);
                      navigate('/checkout', {
                        state: {
                          hotelDetails: { ...hotel },
                          roomDetails: {
                            ...room,
                            idHabitacion: room.id_habitacion,
                            roomType: tiposHabitacion[room.tipo_habitacion],
                            roomPrice: room.precioxnoche,
                            capacidadPersonas: room.capacidad_personas
                          }
                        }
                      });
                    }}>
                      Reservar
                    </Button>

                    <Comentarios idHabitacion={room.id_habitacion} />


                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </React.Fragment>
          ))
        ) : (
          <Col>
            <p className="mt-4">No se encontraron hoteles. Por favor, intenta nuevamente con diferentes criterios de búsqueda.</p>
          </Col>
        )}
      </Row>
    </Container>
  );
};

export default HomePage;
