import React, { useState } from 'react';
import { Container, Form, Button, Alert, Row, Col } from 'react-bootstrap';

const AddHotelPage = () => {
  const [hotelData, setHotelData] = useState({
    id_cadena: 100, // Suponiendo un valor predeterminado para el ejemplo
    nombre: '',
    pais: '',
    ciudad: '',
    direccion: '',
    checkin: '15:00:00',
    checkout: '11:00:00',
  });

  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleHotelChange = (e) => {
    const { name, value } = e.target;
    setHotelData(prev => ({ ...prev, [name]: value }));
  };

  const submitHotel = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(hotelData),
      });

      if (!response.ok) {
        throw new Error('Error al crear hotel');
      }

      setSuccessMessage('Hotel creado exitosamente');
      setHotelData({
        id_cadena: 100,
        nombre: '',
        pais: '',
        ciudad: '',
        direccion: '',
        checkin: '15:00:00',
        checkout: '11:00:00',
      }); // Reiniciar el formulario
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <Container>
      <h1>Agregar Nuevo Hotel</h1>
      <Form>
        <Row>
          <Col>
            <Form.Group controlId="formHotelNombre">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nombre del hotel"
                name="nombre"
                value={hotelData.nombre}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formHotelPais">
              <Form.Label>País</Form.Label>
              <Form.Control
                type="text"
                placeholder="País"
                name="pais"
                value={hotelData.pais}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col>
            <Form.Group controlId="formHotelCiudad">
              <Form.Label>Ciudad</Form.Label>
              <Form.Control
                type="text"
                placeholder="Ciudad"
                name="ciudad"
                value={hotelData.ciudad}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formHotelDireccion">
              <Form.Label>Dirección</Form.Label>
              <Form.Control
                type="text"
                placeholder="Dirección"
                name="direccion"
                value={hotelData.direccion}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Button variant="primary" onClick={submitHotel}>Crear Hotel</Button>
      </Form>
      {successMessage && <Alert variant="success" className="mt-3">{successMessage}</Alert>}
      {errorMessage && <Alert variant="danger" className="mt-3">{errorMessage}</Alert>}
    </Container>
  );
};

export default AddHotelPage;
