import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import Comentarios from './Comentarios';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [fechaIngreso, setFechaIngreso] = useState('');
  const [fechaSalida, setFechaSalida] = useState('');
  const [numeroPersonas, setNumeroPersonas] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [hotelImages, setHotelImages] = useState({});
  const [filterCapacity, setFilterCapacity] = useState('');

  useEffect(() => {
    fetchPaises();
    fetchHotelsAndRooms();
  }, [paisSeleccionado, fechaIngreso, fechaSalida, numeroPersonas]);

  const fetchPaises = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles/paises');
      const data = await response.json();
      setPaises(data);
    } catch (error) {
      setError('Error al cargar paÃ­ses: ' + error.toString());
    }
  };

  const fetchHotelsAndRooms = async () => {
    let url = `http://localhost:8080/habitaciones/disponibles?pais=${paisSeleccionado}&fechaIngreso=${fechaIngreso}&fechaSalida=${fechaSalida}&numeroPersonas=${numeroPersonas}`;
    try {
      const response = await fetch(url);
      const data = await response.json();
      setHotels(data);
      data.forEach(hotel => {
        fetchHotelImages(hotel.id);
      });
    } catch (error) {
      setError('Error al cargar hoteles: ' + error.toString());
    }
  };

  const fetchHotelImages = async (hotelId) => {
    try {
      const response = await fetch(`http://localhost:8080/hoteles/${hotelId}/imagenes`);
      const images = await response.json();
      setHotelImages(prev => ({ ...prev, [hotelId]: images }));
    } catch (error) {
      console.error('Error fetching hotel images:', error);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchHotelsAndRooms();
  };

  return (
    <Container className="my-5">
      <Row>
        <Col md={12}>
          <h2>Buscar Habitaciones Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSearch}>
            <Form.Group controlId="pais">
              <Form.Label>PaÃ­s</Form.Label>
              <Form.Control as="select" value={paisSeleccionado} onChange={(e) => setPaisSeleccionado(e.target.value)}>
                <option value="">Seleccione un paÃ­s</option>
                {paises.map((pais) => (
                  <option key={pais} value={pais}>{pais}</option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="fechaIngreso">
              <Form.Label>Fecha de Ingreso</Form.Label>
              <Form.Control type="date" value={fechaIngreso} onChange={(e) => setFechaIngreso(e.target.value)} />
            </Form.Group>
            <Form.Group controlId="fechaSalida">
              <Form.Label>Fecha de Salida</Form.Label>
              <Form.Control type="date" value={fechaSalida} onChange={(e) => setFechaSalida(e.target.value)} />
            </Form.Group>
            <Form.Group controlId="numeroPersonas">
              <Form.Label>NÃºmero de Personas</Form.Label>
              <Form.Control type="number" value={numeroPersonas} onChange={(e) => setNumeroPersonas(e.target.value)} />
            </Form.Group>
            <Button variant="primary" type="submit">Buscar</Button>
          </Form>
        </Col>
      </Row>
      <Row>
        {hotels.map(hotel => (
          <Col key={hotel.id} md={4}>
            <Card className="mb-4">
              <Carousel>
                {hotelImages[hotel.id]?.map((img, index) => (
                  <Carousel.Item key={index}>
                    <img className="d-block w-100" src={img.url || defaultRoomImage} alt={`Slide ${index}`} />
                  </Carousel.Item>
                ))}
              </Carousel>
              <Card.Body>
                <Card.Title>{hotel.nombre}</Card.Title>
                <Card.Text>
                  {hotel.descripcion}
                  <br />
                  {hotel.direccion}
                </Card.Text>
                <Button variant="primary" onClick={() => navigate(`/hoteles/${hotel.id}`)}>Ver Detalles</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default HomePage;
