import React, { useState } from 'react';
import { Container, Form, Button, Alert, Card, Row, Col } from 'react-bootstrap';

const AddHotelPage = () => {
  const [hotelData, setHotelData] = useState({
    id_cadena: 100,
    nombre: '',
    pais: '',
    ciudad: '',
    direccion: '',
    checkin: '15:00:00',
    checkout: '11:00:00',
  });

  const [habitaciones, setHabitaciones] = useState([]);
  const [nuevaHabitacion, setNuevaHabitacion] = useState({
    numero_habitacion: '',
    capacidad_personas: '',
    tipo_habitacion: '',
    precioxpersona: '',
    precioxnoche: '',
    valuacion: '',
  });

  const [showFormHabitacion, setShowFormHabitacion] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleHotelChange = (e) => {
    const { name, value } = e.target;
    setHotelData((prev) => ({ ...prev, [name]: value }));
  };

  const handleHabitacionChange = (e) => {
    const { name, value } = e.target;
    setNuevaHabitacion((prev) => ({ ...prev, [name]: value }));
  };

  const addHabitacion = () => {
    setHabitaciones((prev) => [...prev, nuevaHabitacion]);
    setNuevaHabitacion({
      numero_habitacion: '',
      capacidad_personas: '',
    tipo_habitacion: '',
    precioxpersona: '',
    precioxnoche: '',
    valuacion: '',
    });
  };

  const submitHotel = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(hotelData),
      });

      if (!response.ok) {
        throw new Error('Error al crear hotel');
      }

      const data = await response.json();
      for (const habitacion of habitaciones) {
        await fetch('http://localhost:8080/habitaciones', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ ...habitacion, id_hotel: data.id }),
        });
      }

      setSuccessMessage('Hotel y habitaciones creados exitosamente');
      setHabitaciones([]);
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <Container>
      <h1>Agregar Nuevo Hotel</h1>
      <Form>
        <Row>
          <Col>
            <Form.Group controlId="formHotelNombre">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nombre del hotel"
                name="nombre"
                value={hotelData.nombre}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formHotelPais">
              <Form.Label>País</Form.Label>
              <Form.Control
                type="text"
                placeholder="País"
                name="pais"
                value={hotelData.pais}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col>
            <Form.Group controlId="formHotelCiudad">
              <Form.Label>Ciudad</Form.Label>
              <Form.Control
                type="text"
                placeholder="Ciudad"
                name="ciudad"
                value={hotelData.ciudad}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formHotelDireccion">
              <Form.Label>Dirección</Form.Label>
              <Form.Control
                type="text"
                placeholder="Dirección"
                name="direccion"
                value={hotelData.direccion}
                onChange={handleHotelChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Button variant="primary" onClick={() => setShowFormHabitacion(!showFormHabitacion)}>
          {showFormHabitacion ? 'Ocultar Formulario de Habitaciones' : 'Agregar Habitaciones'}
        </Button>
        <Button variant="success"
onClick={submitHotel} style={{ marginLeft: '10px' }}>Crear Hotel</Button>
</Form>

{showFormHabitacion && (
  <Card className="mt-3">
    <Card.Body>
      <h5>Agregar Habitación</h5>
      <Form>
        <Row className="mb-3">
          <Col>
            <Form.Group controlId="formNumeroHabitacion">
              <Form.Label>Número de Habitación</Form.Label>
              <Form.Control
                type="text"
                placeholder="Número de Habitación"
                name="numero_habitacion"
                value={nuevaHabitacion.numero_habitacion}
                onChange={handleHabitacionChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formCapacidadPersonas">
              <Form.Label>Capacidad de Personas</Form.Label>
              <Form.Control
                type="number"
                placeholder="Capacidad de Personas"
                name="capacidad_personas"
                value={nuevaHabitacion.capacidad_personas}
                onChange={handleHabitacionChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Group controlId="formTipoHabitacion">
              <Form.Label>Tipo de Habitación</Form.Label>
              <Form.Control
                type="text"
                placeholder="Tipo de Habitación"
                name="tipo_habitacion"
                value={nuevaHabitacion.tipo_habitacion}
                onChange={handleHabitacionChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formPrecioXPersona">
              <Form.Label>Precio por Persona</Form.Label>
              <Form.Control
                type="number"
                placeholder="Precio por Persona"
                name="precioxpersona"
                value={nuevaHabitacion.precioxpersona}
                onChange={handleHabitacionChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col>
            <Form.Group controlId="formPrecioXNoche">
              <Form.Label>Precio por Noche</Form.Label>
              <Form.Control
                type="number"
                placeholder="Precio por Noche"
                name="precioxnoche"
                value={nuevaHabitacion.precioxnoche}
                onChange={handleHabitacionChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formValuacion">
              <Form.Label>Valoración</Form.Label>
              <Form.Control
                type="number"
                placeholder="Valoración"
                name="valuacion"
                value={nuevaHabitacion.valuacion}
                onChange={handleHabitacionChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Button variant="primary" onClick={addHabitacion}>Agregar a Lista</Button>
      </Form>
    </Card.Body>
  </Card>
)}

{habitaciones.length > 0 && (
  <div className="mt-3">
    <h5>Habitaciones Agregadas</h5>
    {habitaciones.map((habitacion, index) => (
      <Alert key={index} variant="success">
        Habitación {habitacion.numero_habitacion} - Tipo: {habitacion.tipo_habitacion} - Precio por Noche: {habitacion.precioxnoche}
        <CloseButton onClick={() => {
          setHabitaciones(habitaciones.filter((_, i) => i !== index));
        }} />
      </Alert>
    ))}
  </div>
)}

{successMessage && <Alert variant="success">{successMessage}</Alert>}
{errorMessage && <Alert variant="danger">{errorMessage}</Alert>}
</Container>
);
};

export default AddHotelPage;
