import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import Comentarios from './Comentarios';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [fechaIngreso, setFechaIngreso] = useState('');
  const [fechaSalida, setFechaSalida] = useState('');
  const [numeroPersonas, setNumeroPersonas] = useState(1);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchPaises();
  }, []);

  const fetchPaises = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles/paises');
      if (!response.ok) throw new Error('Error al cargar los países');
      const data = await response.json();
      setPaises(data);
    } catch (error) {
      console.error('Error fetching countries:', error);
      setError('Error al cargar los países: ' + error.message);
    }
  };

  const fetchHotelsAndRooms = async () => {
    let url = `http://localhost:8080/habitaciones/buscar?pais=${paisSeleccionado}&fechaIngreso=${fechaIngreso}&fechaSalida=${fechaSalida}&numeroPersonas=${numeroPersonas}`;
    try {
      const response = await fetch(url);
      if (!response.ok) throw new Error('Error al cargar hoteles y habitaciones');
      const data = await response.json();
      setHotels(data);
    } catch (error) {
      console.error('Error fetching hotels and rooms:', error);
      setError('Error al cargar hoteles y habitaciones: ' + error.message);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchHotelsAndRooms();
  };

  return (
    <Container className="my-5">
      <Form onSubmit={handleSearch}>
        <Row>
          <Col>
            <Form.Group controlId="formPais">
              <Form.Label>País</Form.Label>
              <Form.Control as="select" value={paisSeleccionado} onChange={(e) => setPaisSeleccionado(e.target.value)}>
                {paises.map((pais) => (
                  <option key={pais} value={pais}>{pais}</option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formFechaIngreso">
              <Form.Label>Fecha de Ingreso</Form.Label>
              <Form.Control type="date" value={fechaIngreso} onChange={(e) => setFechaIngreso(e.target.value)} />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formFechaSalida">
              <Form.Label>Fecha de Salida</Form.Label>
              <Form.Control type="date" value={fechaSalida} onChange={(e) => setFechaSalida(e.target.value)} />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formNumeroPersonas">
              <Form.Label>Número de Personas</Form.Label>
              <Form.Control type="number" value={numeroPersonas} onChange={(e) => setNumeroPersonas(e.target.value)} />
            </Form.Group>
          </Col>
        </Row>
        <Button variant="primary" type="submit">Buscar</Button>
      </Form>

      {error && <Alert variant="danger">{error}</Alert>}

      <Row className="mt-3">
        {hotels.map((hotel) => (
          <Col key={hotel.idHotel} md={4}>
            <Card>
              <Card.Img variant="top" src={hotel.imagenHotelUrl || defaultRoomImage} />
              <Card.Body>
                <Card.Title>{hotel.nombreHotel}</Card.Title>
                <Card.Text>{hotel.ciudadHotel}, {hotel.paisHotel}</Card.Text>
                <Button variant="primary" onClick={() => navigate(`/hotels/${hotel.idHotel}`)}>Ver detalles</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default HomePage;
