import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

// Si reserva.tipoHabitacion ya es un número que representa el tipo de habitación, 
// simplemente asegúrate de que se use correctamente en el estado inicial.
// La clave aquí es asegurarse de que el tipo de habitación que se muestra en el modal
// corresponda al tipo de habitación de la reserva que se está editando.

const EditReservationPage = ({ show, handleClose, reserva, actualizarReserva }) => {
  const [checkIn, setCheckIn] = useState(reserva.fechaIngreso);
  const [checkOut, setCheckOut] = useState(reserva.fechaSalida);
  // Se inicializa tipoHabitacion con el valor actual del tipo de habitación de la reserva
  const [tipoHabitacion, setTipoHabitacion] = useState(reserva.tipoHabitacion.toString());

  const handleSubmit = async (e) => {
    e.preventDefault();
    const reservaActualizada = {
      ...reserva,
      fechaIngreso: checkIn,
      fechaSalida: checkOut,
      // Convertir tipoHabitacion a número antes de enviar
      tipoHabitacion: parseInt(tipoHabitacion),
    };

    console.log('Enviando reserva actualizada:', reservaActualizada);

    try {
      const response = await fetch(`http://localhost:8080/reservas/${reserva.idReserva}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(reservaActualizada),
      });

      if (!response.ok) {
        throw new Error(`No se pudo actualizar la reserva, status: ${response.status}`);
      }

      console.log('Reserva actualizada con éxito');
      handleClose(); // Cerrar modal si la actualización es exitosa
      actualizarReserva(reservaActualizada); // Asegúrate de implementar esta función en el componente padre para actualizar el estado global o refrescar los datos.
    } catch (error) {
      console.error('Error al actualizar la reserva:', error);
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar Reserva</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          {/* Campos de fecha */}
          <Form.Group controlId="formCheckIn">
            <Form.Label>Check-in</Form.Label>
            <Form.Control
              type="date"
              value={checkIn}
              onChange={(e) => setCheckIn(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formCheckOut">
            <Form.Label>Check-out</Form.Label>
            <Form.Control
              type="date"
              value={checkOut}
              onChange={(e) => setCheckOut(e.target.value)}
            />
          </Form.Group>
          {/* Selector de tipo de habitación */}
          <Form.Group controlId="formTipoHabitacion">
            <Form.Label>Tipo de Habitación</Form.Label>
            <Form.Control as="select" value={tipoHabitacion} onChange={(e) => setTipoHabitacion(e.target.value)}>
              <option value="1">Doble</option>
              <option value="2">Junior Suite</option>
              <option value="3">Suite</option>
              <option value="4">Gran Suite</option>
            </Form.Control>
          </Form.Group>
          <Button variant="primary" type="submit">Guardar Cambios</Button>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>Cerrar</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditReservationPage;
