import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, PointElement, LineElement, ArcElement } from 'chart.js';
import { Bar, Line, Pie } from 'react-chartjs-2';
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Analiticos = () => {
    const [data, setData] = useState({
        busquedas: [],
        paisesData: [],
        evolucionData: [],
        tipoAccesoData: { web: 0, rest: 0 },
        paises: []
    });
    const [filtros, setFiltros] = useState({
        pais: '',
        fechaDesde: '',
        fechaHasta: '',
        tipoAcceso: ''
    });

    useEffect(() => {
        fetchDatos();
    }, [filtros]);

    const fetchDatos = async () => {
        const params = {
            params: filtros
        };
        try {
            const busquedasResponse = await axios.get('http://localhost:8080/analiticos/busquedas', params);
            setData(prev => ({
                ...prev,
                busquedas: busquedasResponse.data,
                paisesData: busquedasResponse.data.paisesData,
                evolucionData: busquedasResponse.data.evolucionData,
                tipoAccesoData: busquedasResponse.data.tipoAccesoData
            }));
        } catch (error) {
            console.error('Error al cargar los datos de analítica:', error);
        }
    };

    const handleFiltroChange = (e) => {
        setFiltros({
            ...filtros,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmitFiltros = (e) => {
        e.preventDefault();
        fetchDatos();
    };

    return (
        <Container fluid>
            <Row>
                <Col md={12}>
                    <h2>Análisis de Búsquedas</h2>
                    <Form onSubmit={handleSubmitFiltros}>
                        <Row>
                            <Col>
                                <Form.Group controlId="filtroPais">
                                    <Form.Label>País</Form.Label>
                                    <Form.Control as="select" name="pais" value={filtros.pais} onChange={handleFiltroChange}>
                                        <option value="">Seleccione un país</option>
                                        {data.paises.map((pais, index) => (
                                            <option key={index} value={pais}>{pais}</option>
                                        ))}
                                    </Form.Control>
                                </Form.Group>
                            </Col>
                            <Col>
                                <Form.Group controlId="filtroFechaDesde">
                                    <Form.Label>Fecha Desde</Form.Label>
                                    <Form.Control type="date" name="fechaDesde" value={filtros.fechaDesde} onChange={handleFiltroChange} />
                                </Form.Group>
                            </Col>
                            <Col>
                                <Form.Group controlId="filtroFechaHasta">
                                    <Form.Label>Fecha Hasta</Form.Label>
                                    <Form.Control type="date" name="fechaHasta" value={filtros.fechaHasta} onChange={handleFiltroChange} />
                                </Form.Group>
                            </Col>
                            <Col>
                                <Form.Group controlId="filtroTipoAcceso">
                                    <Form.Label>Tipo de Acceso</Form.Label>
                                    <Form.Control as="select" name="tipoAcceso" value={filtros.tipoAcceso} onChange={handleFiltroChange}>
                                        <option value="">Seleccione</option>
                                        <option value="web">Web</option>
                                        <option value="rest">REST</option>
                                    </Form.Control>
                                </Form.Group>
                            </Col>
                            <Col md={2} className="align-self-end">
                                <Button type="submit" className="w-100">Aplicar Filtros</Button>
                            </Col>
                        </Row>
                    </Form>
                </Col>
            </Row>
            <Row className="mt-4">
                <Col md={6}>
                    <Bar data={{
                        labels: data.paisesData.map(d => d.pais),
                        datasets: [{
                            label: 'Número de búsquedas por país',
                            data: data.paisesData.map(d => d.cantidad),
                            backgroundColor: 'rgba(255, 99, 132, 0.5)',
                        }],
                    }} options={{ responsive: true }} />
                </Col>
                <Col md={6}>
                    <Line data={{
                        labels: data.evolucionData.map(d => d.fecha),
                        datasets: [{
                            label: 'Búsquedas a lo largo del tiempo',
                            data: data.evolucionData.map(d => d.cantidad),
                            fill: false,
                            borderColor: 'rgb(75, 192, 192)',
                            tension: 0.1
                        }]
                    }} options={{ scales: { y: { beginAtZero: true } } }} />
                </Col>
            </Row>
            <Row>
                <Col md={6}>
                    <h4>Distribución del Tipo de Acceso</h4>
                    <Pie data={{
                        labels: ['Web', 'REST'],
                        datasets: [{
                            label: 'Tipo de Acceso',
                            data: [data.tipoAccesoData.web, data.tipoAccesoData.rest],
                            backgroundColor: [
                                'rgba(255, 205, 86, 0.7)',
                                'rgba(54, 162, 235, 0.7)'
                            ],
                            hoverOffset: 4
                        }]
                    }} options={{ responsive: true }} />
                </Col>
            </Row>
        </Container>
    );
};

export default Analiticos;
