import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Alert } from 'react-bootstrap';

const HotelSearchForm = () => {
  const [destinos, setDestinos] = useState([]);
  const [destinoSeleccionado, setDestinoSeleccionado] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [numeroHuéspedes, setNumeroHuéspedes] = useState(1);
  const [hotelesDisponibles, setHotelesDisponibles] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    // Carga inicial de destinos
    fetchDestinos();
  }, []);

  const fetchDestinos = async () => {
    try {
      // Asegúrate de que tu endpoint devuelve solo los países únicos
      const response = await fetch('http://localhost:8080/hoteles/destinos');
      if (!response.ok) throw new Error('Error al cargar los destinos');
      const data = await response.json();
      setDestinos(data);
    } catch (error) {
      setError('Error al cargar los destinos');
    }
  };

  const buscarHoteles = async (e) => {
    e.preventDefault();
    // Asegúrate de ajustar la URL y los parámetros según tu API
    try {
      const response = await fetch(`http://localhost:8080/hoteles/buscar?destino=${destinoSeleccionado}&fechaInicio=${fechaInicio}&fechaFin=${fechaFin}&huéspedes=${numeroHuéspedes}`);
      if (!response.ok) throw new Error('Error al buscar hoteles');
      const data = await response.json();
      setHotelesDisponibles(data);
    } catch (error) {
      setError('Error al buscar hoteles disponibles');
    }
  };

  return (
    <Container>
      <Row>
        <Col>
          <h2>Buscar Hoteles y Habitaciones Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={buscarHoteles}>
            <Form.Group>
              <Form.Label>Destino</Form.Label>
              <Form.Control as="select" onChange={(e) => setDestinoSeleccionado(e.target.value)}>
                <option value="">Seleccione un destino</option>
                {destinos.map(destino => (
                  <option key={destino} value={destino}>{destino}</option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>Fecha de Check-In</Form.Label>
              <Form.Control type="date" value={fechaInicio} onChange={(e) => setFechaInicio(e.target.value)} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Fecha de Check-Out</Form.Label>
              <Form.Control type="date" value={fechaFin} onChange={(e) => setFechaFin(e.target.value)} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Número de Huéspedes</Form.Label>
              <Form.Control type="number" value={numeroHuéspedes} onChange={(e) => setNumeroHuéspedes(e.target.value)} />
            </Form.Group>
            <Button type="submit">Buscar</Button>
          </Form>
        </Col>
      </Row>
      <Row>
        {/* Aquí puedes agregar la lógica para mostrar los hoteles y habitaciones disponibles en base a la respuesta de buscarHoteles */}
      </Row>
    </Container>
  );
};

export default HotelSearchForm;
