import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import HotelSearchForm from './HotelSearchForm';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Inicia una carga inicial de todos los hoteles.
    fetchHotelsAndRooms();
  }, []);

  const fetchHotelsAndRooms = async (pais = '') => {
    let url = `http://localhost:8080/hoteles${pais ? `?pais=${encodeURIComponent(pais)}` : ''}`;
    try {
      const response = await fetch(url);
      if (!response.ok) throw new Error('Error al cargar hoteles');
      const hotelsData = await response.json();
      setHotels(hotelsData); // Asumiendo que el backend ya filtra correctamente.
    } catch (error) {
      console.error('Error fetching hotels:', error);
    }
  };

  const handleSearch = (pais) => {
    fetchHotelsAndRooms(pais);
  };

  return (
    <Container className="my-5">
      <HotelSearchForm onSearch={handleSearch} />
      {hotels.length > 0 ? (
        hotels.map(hotel => (
          <Row key={hotel.id_hotel} className="mb-3">
            <Col md={12}>
              <Card>
                <Card.Img variant="top" src={defaultRoomImage} />
                <Card.Body>
                  <Card.Title>{hotel.nombre}</Card.Title>
                  <Card.Text>{hotel.ciudad}, {hotel.pais}</Card.Text>
                  <Card.Text>Dirección: {hotel.direccion}</Card.Text>
                  <Button variant="primary" onClick={() => navigate('/checkout', { state: { hotelDetails: hotel } })}>
                    Ver más
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        ))
      ) : (
        <p>No se encontraron hoteles. Por favor, intenta nuevamente con diferentes criterios de búsqueda.</p>
      )}
    </Container>
  );
};

export default HomePage;
