// HotelSearchForm.jsx
import React, { useState, useEffect } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';

const HotelSearchForm = () => {
  const [destinos, setDestinos] = useState([]);
  const [searchParams, setSearchParams] = useState({
    destino: '',
    checkIn: '',
    checkOut: '',
    huespedes: 1,
  });

  useEffect(() => {
    // Suponiendo un endpoint /hoteles/paises para obtener los países de la tabla de hoteles
    fetch('http://localhost:8080/hoteles/paises')
      .then((response) => response.json())
      .then((data) => setDestinos(data))
      .catch((error) => console.error('Error fetching destinos:', error));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Aquí realizarías la llamada a tu API para buscar hoteles y habitaciones disponibles
    // Este ejemplo asume la existencia de un endpoint que soporte estos parámetros
    console.log('Buscando con parámetros:', searchParams);
  };

  return (
    <Form onSubmit={handleSubmit} className="search-form">
      <Row>
        <Col sm={12} md={3}>
          <Form.Group controlId="formDestino">
            <Form.Label>Destino</Form.Label>
            <Form.Control
              as="select"
              name="destino"
              value={searchParams.destino}
              onChange={handleInputChange}
            >
              <option value="">Seleccione un destino</option>
              {destinos.map(destino => (
                <option key={destino} value={destino}>{destino}</option>
              ))}
            </Form.Control>
          </Form.Group>
        </Col>
        <Col sm={6} md={3}>
          <Form.Group controlId="formCheckIn">
            <Form.Label>Check-in</Form.Label>
            <Form.Control
              type="date"
              name="checkIn"
              value={searchParams.checkIn}
              onChange={handleInputChange}
            />
          </Form.Group>
        </Col>
        <Col sm={6} md={3}>
          <Form.Group controlId="formCheckOut">
            <Form.Label>Check-out</Form.Label>
            <Form.Control
              type="date"
              name="checkOut"
              value={searchParams.checkOut}
              onChange={handleInputChange}
            />
          </Form.Group>
        </Col>
        <Col sm={6} md={3}>
          <Form.Group controlId="formHuespedes">
            <Form.Label>Número de Huéspedes</Form.Label>
            <Form.Control
              type="number"
              name="huespedes"
              value={searchParams.huespedes}
              onChange={handleInputChange}
              min="1"
            />
          </Form.Group>
        </Col>
      </Row>
      <Button variant="primary" type="submit" className="mt-3">
        Buscar Hoteles
      </Button>
    </Form>
  );
};

export default HotelSearchForm;
