import React, { useState, useEffect } from 'react';
import { Button, Form, Card, Container, Row } from 'react-bootstrap';
import { useUser } from './UserContext';

function Comentarios({ idHabitacion }) {
  const [comentarios, setComentarios] = useState([]);
  const [textoComentario, setTextoComentario] = useState('');
  const [rating, setRating] = useState(1);
  const [idComentarioPadre, setIdComentarioPadre] = useState(null);
  const { user } = useUser();

  useEffect(() => {
    fetchComentarios();
  }, [idHabitacion, idComentarioPadre]);

  const fetchComentarios = async () => {
    try {
      const response = await fetch(`http://localhost:8080/comentarios/por-habitacion/${idHabitacion}`);
      if (response.ok) {
        const data = await response.json();
        setComentarios(data);
      } else {
        console.error('Error al recuperar los comentarios');
      }
    } catch (error) {
      console.error('Error al recuperar los comentarios:', error);
    }
  };

  const crearComentario = async (e) => {
    e.preventDefault();
    if (!user || !user.id) {
      alert('Debes estar registrado y haber iniciado sesión para realizar un comentario.');
      return;
    }

    const comentario = {
      idHabitacion,
      idUsuario: user.id,
      textoComentario,
      rating,
      idComentarioPadre
    };

    try {
      await fetch('http://localhost:8080/comentarios', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(comentario)
      });
      fetchComentarios();
      setTextoComentario('');
      setRating(1);
      setIdComentarioPadre(null); // Resetear el comentario padre después de enviar
    } catch (error) {
      console.error('Error al crear el comentario:', error);
    }
  };

  const responderAComentario = (idComentario) => {
    setIdComentarioPadre(idComentario);
    // Mover el foco al campo de entrada para la respuesta aquí si es necesario
  };

  // Función recursiva para renderizar comentarios y sus respuestas
  const renderComentarios = (listaDeComentarios) => {
    return listaDeComentarios.map((comentario) => (
      <Card key={comentario.idComentario} className="mb-3">
        <Card.Body>
          <Card.Title>{comentario.nombreUsuario} - {comentario.rating} estrellas</Card.Title>
          <Card.Text>{comentario.textoComentario}</Card.Text>
          <Button variant="link" onClick={() => responderAComentario(comentario.idComentario)}>Responder</Button>
          {/* Aquí puedes añadir lógica adicional si tienes respuestas a este comentario */}
        </Card.Body>
      </Card>
    ));
  };

  return (
    <Container>
      <Row>
        <Form onSubmit={crearComentario}>
          <Form.Group controlId="textoComentario">
            <Form.Label>Comentario</Form.Label>
            <Form.Control as="textarea" rows={3} value={textoComentario} onChange={(e) => setTextoComentario(e.target.value)} />
          </Form.Group>
          <Form.Group controlId="rating">
            <Form.Label>Rating</Form.Label>
            <Form.Control as="select" value={rating} onChange={(e) => setRating(e.target.value)}>
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option>5</option>
            </Form.Control>
          </Form.Group>
          {idComentarioPadre && (
            <div>Respondiendo al comentario #{idComentarioPadre}</div>
          )}
          <Button variant="primary" type="submit">Enviar Comentario</Button>
        </Form>
      </Row>
      <Row>
        {renderComentarios(comentarios)}
      </Row>
    </Container>
  );
}

export default Comentarios;
