import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import HotelSearchForm from './HotelSearchForm'; // Asumiendo que esta es la ruta correcta

const HomePage = () => {
    const [hotels, setHotels] = useState([]);

    useEffect(() => {
        // Llamada inicial para cargar todos los hoteles
        fetchHotelsAndRooms();
    }, []);

    const fetchHotelsAndRooms = async (pais = '') => {
        let url = 'http://localhost:8080/hoteles';
        if (pais) {
            url += `?pais=${encodeURIComponent(pais)}`;
        }
        try {
            const response = await fetch(url);
            if (!response.ok) throw new Error('Error al cargar hoteles');
            const hotelsData = await response.json();
            setHotels(hotelsData);
        } catch (error) {
            console.error('Error fetching hotels:', error);
        }
    };

    const handleSearch = (pais) => {
        fetchHotelsAndRooms(pais);
    };

    return (
        <Container className="my-5">
            <HotelSearchForm onSearch={handleSearch} />
            {/* Renderizaci√≥n condicional de hoteles */}
        </Container>
    );
};

export default HomePage;
