import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import HotelSearchForm from './HotelSearchForm';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  // Definición de los tipos de habitación para una representación más amigable.
  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };

  useEffect(() => {
    // Carga inicial de todos los hoteles.
    fetchHotelsAndRooms();
  }, []);

  // Función para buscar hoteles, con la capacidad de filtrar por país.
  const fetchHotelsAndRooms = async (pais = '') => {
    // Construcción de la URL con filtrado por país si se especifica uno.
    let url = 'http://localhost:8080/hoteles';
    if (pais) {
      url += `?pais=${encodeURIComponent(pais)}`;
    }

    try {
      const response = await fetch(url);
      if (!response.ok) throw new Error('Error al cargar hoteles');
      const hotelsData = await response.json();

      // Ya no se necesita filtrar en el cliente, el backend debería devolver la lista correcta.
      console.log(`Hoteles cargados:`, hotelsData); // Log para seguimiento.
      setHotels(hotelsData); // Actualiza el estado con los hoteles filtrados.
    } catch (error) {
      console.error('Error fetching hotels:', error);
    }
  };

  // Manejador para la búsqueda basada en el país seleccionado.
  const handleSearch = (pais) => {
    console.log(`Buscando hoteles en el país: ${pais}`); // Log para seguimiento.
    fetchHotelsAndRooms(pais);
  };


  return (
    <Container className="my-5">
      <HotelSearchForm onSearch={handleSearch} />
      {hotels.length > 0 ? (
        hotels.map((hotel) => (
          <React.Fragment key={hotel.id_hotel}>
            <Row>
              <Col md={12}>
                <h2>Hotel: {hotel.nombre}</h2>
                <p>{hotel.ciudad}, {hotel.pais}</p>
                <p>Dirección: {hotel.direccion}</p>
              </Col>
            </Row>
            {hotel.rooms && hotel.rooms.length > 0 ? (
              hotel.rooms.map((room) => (
                <Col key={room.id_habitacion} md={4}>
                  <Card className="mb-3">
                    <Card.Img variant="top" src={defaultRoomImage} />
                    <Card.Body>
                      <Card.Title>Habitación: {tiposHabitacion[room.tipo_habitacion]}</Card.Title>
                      <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                      <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                      <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                      <Card.Text>Valoración: {room.valuacion} estrellas</Card.Text>
                      <Button variant="primary" onClick={() => navigate('/checkout', { state: { hotelDetails: hotel, roomDetails: room } })}>Reservar</Button>
                    </Card.Body>
                  </Card>
                </Col>
              ))
            ) : (
              <Col>
                <p>Este hotel no tiene habitaciones disponibles en este momento.</p>
              </Col>
            )}
          </React.Fragment>
        ))
      ) : (
        <p>No se encontraron hoteles. Por favor, intenta nuevamente con diferentes criterios de búsqueda.</p>
      )}
    </Container>
  );
};

export default HomePage;
