import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import HotelSearchForm from './HotelSearchForm'; 

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();


  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };


  useEffect(() => {
    fetchHotelsAndRooms();
  }, []);

  const fetchHotelsAndRooms = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles');
      if (!response.ok) {
        throw new Error('Error al cargar hoteles');
      }
      const data = await response.json();
      console.log('Datos recibidos de la API:', data); // Agrega este registro para depurar
      // Asegúrate de que data es un arreglo antes de intentar establecer el estado
      if (Array.isArray(data)) {
        setHotels(data);
      } else {
        console.error('Formato de datos inesperado:', data);
        setHotels([]); // Establece un arreglo vacío si los datos no tienen el formato esperado
      }
    } catch (error) {
      console.error('Error al cargar hoteles:', error);
      setHotels([]); // Establece un arreglo vacío en caso de error
    }
  };


  // Define la función handleSearch que será pasada a HotelSearchForm
  const handleSearch = (pais) => {
    fetchHotelsAndRooms(pais);
  };

return (
  <Container className="my-5">
    <HotelSearchForm onSearch={handleSearch} />
    {hotels.length > 0 ? (
      hotels.map((hotel) => (
        <Row key={hotel.id_hotel}>
          <Col md={12}>
            <h2>Hotel: {hotel.nombre}</h2>
            <p>{hotel.ciudad}, {hotel.pais}</p>
            <p>Dirección: {hotel.direccion}</p>
          </Col>
          {hotel.rooms.map((room) => (
            <Col key={room.id_habitacion} md={4}>
              <Card className="mb-3">
                <Card.Body>
                  <Card.Title>Habitación: {tiposHabitacion[room.tipo_habitacion]}</Card.Title>
                  <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                  <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                  <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                  <Card.Text>Valoración: {room.valuacion} estrellas</Card.Text>
                  <Card.Img variant="top" src={defaultRoomImage} />
                  <Button variant="primary" onClick={() => {
                    navigate('/checkout', {
                      state: {
                        hotelDetails: { ...hotel },
                        roomDetails: {
                          ...room,
                          idHabitacion: room.id_habitacion,
                          roomType: tiposHabitacion[room.tipo_habitacion],
                          roomPrice: room.precioxnoche,
                          capacidadPersonas: room.capacidad_personas
                        }
                      }
                    });
                  }}>
                    Reservar
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      ))
    ) : (
      <p>No se encontraron hoteles para los criterios de búsqueda.</p>
    )}
  </Container>
);
};

export default HomePage;
