import React from 'react';
import { Accordion, Container, Card, Button } from 'react-bootstrap';

const FAQs = () => {
  return (
    <Container className="my-5">
      <h2 className="mb-4 text-center">Preguntas Frecuentes</h2>

      {/* Misión, Visión y Objetivos */}
      <section className="mb-5">
        <h3>Nuestra Misión</h3>
        <p>Ofrecer experiencias de alojamiento únicas y memorables, asegurando siempre el máximo confort y calidad en nuestros servicios.</p>

        <h3>Nuestra Visión</h3>
        <p>Ser líderes en la industria hotelera, reconocidos por nuestra innovación, servicio excepcional y compromiso con la sostenibilidad.</p>

        <h3>Objetivos</h3>
        <ul>
          <li>Exceder las expectativas de nuestros huéspedes a través de un servicio personalizado.</li>
          <li>Promover prácticas sostenibles que respeten el medio ambiente y la comunidad.</li>
          <li>Expandir nuestra presencia a nivel global manteniendo altos estándares de calidad.</li>
        </ul>
      </section>

      {/* Preguntas Frecuentes */}
      <Accordion defaultActiveKey="0">
        <Accordion.Item eventKey="0">
          <Accordion.Header>¿Cómo puedo hacer una reserva?</Accordion.Header>
          <Accordion.Body>
            Puede hacer una reserva a través de nuestro sitio web, seleccionando el hotel de su preferencia, las fechas de estancia y completando el proceso de reserva en línea.
          </Accordion.Body>
        </Accordion.Item>

        <Accordion.Item eventKey="1">
          <Accordion.Header>¿Qué medidas de seguridad se están tomando en los hoteles?</Accordion.Header>
          <Accordion.Body>
            Nuestros hoteles siguen estrictas medidas de seguridad y limpieza para garantizar el bienestar de nuestros huéspedes, incluyendo sanitización regular, uso de mascarillas y distanciamiento social.
          </Accordion.Body>
        </Accordion.Item>

        {/* Agrega más preguntas y respuestas según sea necesario */}
      </Accordion>
    </Container>
  );
};

export default FAQs;
