import React, { useState, useEffect } from 'react';

function FAQS() {
    const [faqs, setFaqs] = useState([]);
    const [seccionesGenerales, setSeccionesGenerales] = useState([]);
    const [nuevaFaq, setNuevaFaq] = useState({ pregunta: '', respuesta: '' });
    const [nuevaSeccion, setNuevaSeccion] = useState({ titulo: '', contenido: '' });

    // Cargar las FAQs y las secciones generales al montar el componente
    useEffect(() => {
        fetchFaqs();
        fetchSeccionesGenerales();
    }, []);

    const fetchFaqs = async () => {
        const response = await fetch('http://localhost:8080/faqs');
        const data = await response.json();
        setFaqs(data);
    };

    const fetchSeccionesGenerales = async () => {
        const response = await fetch('http://localhost:8080/secciones-generales');
        const data = await response.json();
        setSeccionesGenerales(data);
    };

    const handleCrearFaq = async (e) => {
        e.preventDefault();
        await fetch('http://localhost:8080/faqs', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(nuevaFaq),
        });
        setNuevaFaq({ pregunta: '', respuesta: '' });
        fetchFaqs();
    };

    const handleCrearSeccion = async (e) => {
        e.preventDefault();
        await fetch('http://localhost:8080/secciones-generales', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(nuevaSeccion),
        });
        setNuevaSeccion({ titulo: '', contenido: '' });
        fetchSeccionesGenerales();
    };

    // Funciones para actualizar y eliminar FAQs y secciones generales...

    return (
        <div>
            <h2>FAQs</h2>
            {faqs.map((faq) => (
                <div key={faq.id}>
                    <p>{faq.pregunta}</p>
                    <p>{faq.respuesta}</p>
                    {/* Botones o enlaces para editar/eliminar la FAQ */}
                </div>
            ))}
            <form onSubmit={handleCrearFaq}>
                <input
                    type="text"
                    value={nuevaFaq.pregunta}
                    onChange={(e) => setNuevaFaq({ ...nuevaFaq, pregunta: e.target.value })}
                    placeholder="Pregunta"
                    required
                />
                <input
                    type="text"
                    value={nuevaFaq.respuesta}
                    onChange={(e) => setNuevaFaq({ ...nuevaFaq, respuesta: e.target.value })}
                    placeholder="Respuesta"
                    required
                />
                <button type="submit">AÃ±adir FAQ</button>
            </form>

            <h2>Secciones Generales</h2>
            {seccionesGenerales.map((seccion) => (
                <div key={seccion.id}>
                    <h3>{seccion.titulo}</h3>
                    <p>{seccion.contenido}</p>
                    {/* Botones o enlaces para editar/eliminar la secciÃ³n */}
                </div>
            ))}
            <form onSubmit={handleCrearSeccion}>
                <input
                    type="text"
                    value={nuevaSeccion.titulo}
                    onChange={(e) => setNuevaSeccion({ ...nuevaSeccion, titulo: e.target.value })}
                    placeholder="TÃ­tulo"
                    required
                />
                <textarea
                    value={nuevaSeccion.contenido}
                    onChange={(e) => setNuevaSeccion({ ...nuevaSeccion, contenido: e.target.value })}
                    placeholder="Contenido"
                    required
                />
                <button type="submit">AÃ±adir SecciÃ³n</button>
            </form>
        </div>
    );
}

export default FAQS;
