import React, { useState, useEffect } from 'react';

function FAQS() {
    const [faqs, setFaqs] = useState([]);
    const [seccionesGenerales, setSeccionesGenerales] = useState([]);
    const [nuevaFaq, setNuevaFaq] = useState({ pregunta: '', respuesta: '' });
    const [nuevaSeccion, setNuevaSeccion] = useState({ titulo: '', contenido: '' });
    const [editarFaq, setEditarFaq] = useState(null);
    const [editarSeccion, setEditarSeccion] = useState(null);

    useEffect(() => {
        fetchFaqs();
        fetchSeccionesGenerales();
    }, []);

    const fetchFaqs = async () => {
        const response = await fetch('http://localhost:8080/faqs');
        const data = await response.json();
        setFaqs(data);
    };

    const fetchSeccionesGenerales = async () => {
        const response = await fetch('http://localhost:8080/secciones-generales');
        const data = await response.json();
        setSeccionesGenerales(data);
    };

    const handleCrearOActualizarFaq = async (e) => {
        e.preventDefault();
        const url = editarFaq ? `http://localhost:8080/faqs/${editarFaq.id}` : 'http://localhost:8080/faqs';
        const method = editarFaq ? 'PUT' : 'POST';

        await fetch(url, {
            method: method,
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(editarFaq || nuevaFaq),
        });

        setNuevaFaq({ pregunta: '', respuesta: '' });
        setEditarFaq(null);
        fetchFaqs();
    };

    const handleCrearOActualizarSeccion = async (e) => {
        e.preventDefault();
        const url = editarSeccion ? `http://localhost:8080/secciones-generales/${editarSeccion.id}` : 'http://localhost:8080/secciones-generales';
        const method = editarSeccion ? 'PUT' : 'POST';

        await fetch(url, {
            method: method,
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(editarSeccion || nuevaSeccion),
        });

        setNuevaSeccion({ titulo: '', contenido: '' });
        setEditarSeccion(null);
        fetchSeccionesGenerales();
    };

    const iniciarEdicionFaq = (faq) => {
        setEditarFaq({ ...faq });
    };

    const iniciarEdicionSeccion = (seccion) => {
        setEditarSeccion({ ...seccion });
    };

    const eliminarFaq = async (id) => {
        await fetch(`http://localhost:8080/faqs/${id}`, { method: 'DELETE' });
        fetchFaqs();
    };

    const eliminarSeccion = async (id) => {
        await fetch(`http://localhost:8080/secciones-generales/${id}`, { method: 'DELETE' });
        fetchSeccionesGenerales();
    };

    return (
        <div>
            <h2>FAQs</h2>
            {faqs.map((faq) => (
                <div key={faq.id}>
                    <p>{faq.pregunta}</p>
                    <p>{faq.respuesta}</p>
                    <button onClick={() => iniciarEdicionFaq(faq)}>Editar</button>
                    <button onClick={() => eliminarFaq(faq.id)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={handleCrearOActualizarFaq}>
                <input
                    type="text"
                    value={editarFaq ? editarFaq.pregunta : nuevaFaq.pregunta}
                    onChange={(e) => editarFaq ? setEditarFaq({ ...editarFaq, pregunta: e.target.value }) : setNuevaFaq({ ...nuevaFaq, pregunta: e.target.value })}
                    placeholder="Pregunta"
                    required
                />
                <input
                    type="text"
                    value={editarFaq ? editarFaq.respuesta : nuevaFaq.respuesta}
                    onChange={(e) => editarFaq ? setEditarFaq({ ...editarFaq, respuesta: e.target.value }) : setNuevaFaq({ ...nuevaFaq, respuesta: e.target.value })}
                    placeholder="Respuesta"
                    required
                />
                <button type="submit">{editarFaq ? 'Actualizar FAQ' : 'Añadir FAQ'}</button>
            </form>

            <h2>Secciones Generales</h2>
            {seccionesGenerales.map((seccion) => (
                <div key={seccion.id}>
                    <h3>{seccion.titulo}</h3>
                    <p>{seccion.contenido}</p>
                    <button onClick={() => iniciarEdicionSeccion(seccion)}>Editar</button>
                    <button onClick={() => eliminarSeccion(seccion.id)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={handleCrearOActualizarSeccion}>
                <input
                    type="text"
                    value={editarSeccion ? editarSeccion.titulo : nuevaSeccion.titulo}
                    onChange={(e) => editarSeccion ? setEditarSeccion({ ...editarSeccion, titulo: e.target.value }) : setNuevaSeccion({ ...nuevaSeccion, titulo: e.target.value })}
                    placeholder="Título"
                    required
                />
                <textarea
                    value={editarSeccion ? editarSeccion.contenido : nuevaSeccion.contenido}
                    onChange={(e) => editarSeccion ? setEditarSeccion({ ...editarSeccion, contenido: e.target.value }) : setNuevaSeccion({ ...nuevaSeccion, contenido: e.target.value })}
                    placeholder="Contenido"
                    required
                />
                <button type="submit">{editarSeccion ? 'Actualizar Sección' : 'Añadir Sección'}</button>
            </form>
        </div>
    );
}

export default FAQS;
