import React, { useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Alert } from 'react-bootstrap';

const HotelSearchForm = ({ onSearch }) => {
  const [destinos, setDestinos] = useState([]);
  const [destinoSeleccionado, setDestinoSeleccionado] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [numeroHuéspedes, setNumeroHuéspedes] = useState(1);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDestinos = async () => {
      try {
        const response = await fetch('http://localhost:8080/hoteles/pais');
        if (!response.ok) throw new Error('Error al cargar los destinos');
        const data = await response.json();
        setDestinos(data);
      } catch (error) {
        setError('Error al cargar los destinos: ' + error.message);
      }
    };
    fetchDestinos();
  }, []);

  const buscarHoteles = async (e) => {
    e.preventDefault();
    console.log(`Buscando hoteles con pais: ${destinoSeleccionado}, fechaInicio: ${fechaInicio}, fechaFin: ${fechaFin}, huéspedes: ${numeroHuéspedes}`);

    try {
      // Asegúrate de ajustar la URL de tu API y los parámetros según sea necesario
      const url = `http://localhost:8080/hoteles/buscar?pais=${destinoSeleccionado}&fechaInicio=${fechaInicio}&fechaFin=${fechaFin}&huéspedes=${numeroHuéspedes}`;
      const response = await fetch(url);
      if (!response.ok) throw new Error('Error al buscar hoteles');
      const data = await response.json();
      onSearch(data); // Envía los datos filtrados al componente padre
    } catch (error) {
      console.error('Error al buscar hoteles disponibles:', error);
      setError('Error al buscar hoteles disponibles. Por favor, intente nuevamente.');
    }
  };



  return (
    <Container>
      <Row>
        <Col>
          <h2>Buscar Hoteles y Habitaciones Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={buscarHoteles}>
            <Form.Group>
              <Form.Label>Destino</Form.Label>
              <Form.Control as="select" value={destinoSeleccionado} onChange={(e) => setDestinoSeleccionado(e.target.value)}>
                <option value="">Seleccione un destino</option>
                {destinos.map((destino, index) => (
                  <option key={index} value={destino}>{destino}</option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>Fecha de Check-In</Form.Label>
              <Form.Control type="date" value={fechaInicio} onChange={(e) => setFechaInicio(e.target.value)} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Fecha de Check-Out</Form.Label>
              <Form.Control type="date" value={fechaFin} onChange={(e) => setFechaFin(e.target.value)} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Número de Huéspedes</Form.Label>
              <Form.Control type="number" value={numeroHuéspedes} onChange={(e) => setNumeroHuéspedes(e.target.value)} />
            </Form.Group>
            <Button type="submit">Buscar</Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default HotelSearchForm;
