import React, { useState, useEffect } from 'react';

function FAQS() {
    const [faqs, setFaqs] = useState([]);
    const [seccionesGenerales, setSeccionesGenerales] = useState([]);
    const [faq, setFaq] = useState({ pregunta: '', respuesta: '', id: null });
    const [seccion, setSeccion] = useState({ titulo: '', contenido: '', id: null });

    useEffect(() => {
        fetchFaqs();
        fetchSeccionesGenerales();
    }, []);

    const fetchFaqs = async () => {
        const response = await fetch('http://localhost:8080/faqs');
        if (response.ok) {
            const data = await response.json();
            setFaqs(data);
        }
    };

    const fetchSeccionesGenerales = async () => {
        const response = await fetch('http://localhost:8080/secciones-generales');
        if (response.ok) {
            const data = await response.json();
            setSeccionesGenerales(data);
        }
    };

    const handleChange = (event, setState) => {
        const { name, value } = event.target;
        setState(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e, item, endpoint, stateSetter, fetchCallback) => {
        e.preventDefault();
        const method = item.id ? 'PUT' : 'POST';
        const url = item.id ? `http://localhost:8080/${endpoint}/${item.id}` : `http://localhost:8080/${endpoint}`;

        const response = await fetch(url, {
            method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(item),
        });

        if (response.ok) {
            stateSetter({ pregunta: '', respuesta: '', id: null }); // For FAQs
            stateSetter({ titulo: '', contenido: '', id: null }); // For Secciones Generales
            fetchCallback();
        }
    };

    const handleEdit = (item, setState) => {
        setState(item);
    };

    const handleDelete = async (id, endpoint, fetchCallback) => {
        const response = await fetch(`http://localhost:8080/${endpoint}/${id}`, {
            method: 'DELETE',
        });
        if (response.ok) {
            fetchCallback();
        }
    };

    return (
        <div>
            <h2>FAQs</h2>
            {faqs.map(faq => (
                <div key={faq.id}>
                    <p>Pregunta: {faq.pregunta}</p>
                    <p>Respuesta: {faq.respuesta}</p>
                    <button onClick={() => handleEdit(faq, setFaq)}>Editar</button>
                    <button onClick={() => handleDelete(faq.id, 'faqs', fetchFaqs)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={(e) => handleSubmit(e, faq, 'faqs', setFaq, fetchFaqs)}>
                <input name="pregunta" value={faq.pregunta} onChange={(e) => handleChange(e, setFaq)} placeholder="Pregunta" required />
                <input name="respuesta" value={faq.respuesta} onChange={(e) => handleChange(e, setFaq)} placeholder="Respuesta" required />
                <button type="submit">Guardar FAQ</button>
            </form>

            <h2>Secciones Generales</h2>
            {seccionesGenerales.map(seccion => (
                <div key={seccion.id}>
                    <p>Título: {seccion.titulo}</p>
                    <p>Contenido: {seccion.contenido}</p>
                    <button onClick={() => handleEdit(seccion, setSeccion)}>Editar</button>
                    <button onClick={() => handleDelete(seccion.id, 'secciones-generales', fetchSeccionesGenerales)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={(e) => handleSubmit(e, seccion, 'secciones-generales', setSeccion, fetchSeccionesGenerales)}>
                <input name="titulo" value={seccion.titulo} onChange={(e) => handleChange(e, setSeccion)} placeholder="Título" required />
                <textarea name="contenido" value={seccion.contenido} onChange={(e) => handleChange(e, setSeccion)} placeholder="Contenido" required />
                <button type="submit">Guardar Sección</button>
            </form>
        </div>
    );
}

export default FAQS;
