import React, { useState, useEffect } from 'react';
import { Button, Form, Card, Container, Row } from 'react-bootstrap';

function Comentarios({ idHabitacion }) {
  const [comentarios, setComentarios] = useState([]);
  const [textoComentario, setTextoComentario] = useState('');
  const [rating, setRating] = useState(1);

  useEffect(() => {
    fetchComentarios();
  }, [idHabitacion]); // Asegúrate de incluir idHabitacion como dependencia para recargar comentarios cuando cambie

  const fetchComentarios = async () => {
    const response = await fetch(`http://localhost:8080/comentarios/por-habitacion/${idHabitacion}`);
    const data = await response.json();
    setComentarios(data);
  };

  const crearComentario = async (e) => {
    e.preventDefault();
    const comentario = { idHabitacion, textoComentario, rating, idUsuario: 1 }; // idUsuario está hardcodeado aquí, ajusta según sea necesario
    const response = await fetch('http://localhost:8080/comentarios', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(comentario),
    });

    if (response.ok) {
      setTextoComentario('');
      setRating(1);
      fetchComentarios(); // Recarga comentarios para incluir el nuevo comentario
    }
  };

  return (
    <div>
      <h5>Comentarios</h5>
      {comentarios.map((comentario) => (
        <Card key={comentario.id} className="mb-2">
          <Card.Body>
            <Card.Text>{comentario.textoComentario}</Card.Text>
            <Card.Subtitle>Rating: {comentario.rating}</Card.Subtitle>
          </Card.Body>
        </Card>
      ))}
      <Form onSubmit={enviarComentario}>
        <Form.Group className="mb-3" controlId="textoComentario">
          <Form.Label>Deja tu comentario</Form.Label>
          <Form.Control as="textarea" rows={3} value={textoComentario} onChange={(e) => setTextoComentario(e.target.value)} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="rating">
          <Form.Label>Rating</Form.Label>
          <Form.Control as="select" value={rating} onChange={(e) => setRating(e.target.value)}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
          </Form.Control>
        </Form.Group>
        <Button variant="primary" type="submit">
          Enviar
        </Button>
      </Form>
    </div>
  );
}

export default Comentarios;
