import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const EditReservationPage = ({ show, handleClose, reserva, actualizarReserva }) => {
    const [checkIn, setCheckIn] = useState(reserva.fechaIngreso);
    const [checkOut, setCheckOut] = useState(reserva.fechaSalida);
    const [tipoHabitacion, setTipoHabitacion] = useState(reserva.tipoHabitacion.toString());
    const [tiposHabitacionDisponibles, setTiposHabitacionDisponibles] = useState([]);

    // Mapeo de IDs a nombres de tipos de habitación
    const translateTipoHabitacion = {
        '1': 'Doble',
        '2': 'Junior Suite',
        '3': 'Suite',
        '4': 'Gran Suite',
    };

    useEffect(() => {
        const fetchTiposHabitacion = async () => {
            // Solo se realiza la llamada si el modal está abierto y si el idHotel está definido
            if (show && reserva?.idHotel) {
                try {
                    const response = await fetch(`http://localhost:8080/reservas/tipos-habitacion-por-hotel?hotelId=${reserva.idHotel}`);
                    if (!response.ok) {
                        throw new Error('Error al obtener tipos de habitación');
                    }
                    const tiposHabitacionDisponibles = await response.json();
                    setTiposHabitacionDisponibles(tiposHabitacionDisponibles);
                } catch (error) {
                    console.error('Error al obtener tipos de habitación:', error);
                }
            }
        };

        fetchTiposHabitacion();
    }, [show, reserva?.idHotel]);


    const handleSubmit = async (e) => {
        e.preventDefault();
        const reservaActualizada = {
            ...reserva,
            fechaIngreso: checkIn,
            fechaSalida: checkOut,
            tipoHabitacion: Number(tipoHabitacion),
        };

        try {
            await actualizarReserva(reservaActualizada);
            handleClose();
        } catch (error) {
            console.error('Error al actualizar la reserva:', error);
        }
    };

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Editar Reserva</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="formCheckIn">
                        <Form.Label>Check-in</Form.Label>
                        <Form.Control
                            type="date"
                            value={checkIn}
                            onChange={(e) => setCheckIn(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formCheckOut">
                        <Form.Label>Check-out</Form.Label>
                        <Form.Control
                            type="date"
                            value={checkOut}
                            onChange={(e) => setCheckOut(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formTipoHabitacion">
    <Form.Label>Tipo de Habitación</Form.Label>
    <Form.Control
        as="select"
        value={tipoHabitacion}
        onChange={(e) => setTipoHabitacion(e.target.value)}
    >
        {tiposHabitacionDisponibles.map((tipoId) => (
            <option key={tipoId} value={tipoId}>
                {translateTipoHabitacion(tipoId)}
            </option>
        ))}
    </Form.Control>
</Form.Group>

                    <Button variant="primary" type="submit">Guardar Cambios</Button>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>Cerrar</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default EditReservationPage;
