import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, PointElement, LineElement, ArcElement } from 'chart.js';
import { Bar, Line, Pie } from 'react-chartjs-2';
import { HeatMapGrid } from 'react-heatmap-grid'; // Make sure to import the HeatMapGrid
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Analiticos = () => {
    const [data, setData] = useState({
        busquedas: [],
        paisesData: [],
        evolucionData: [],
        heatmapData: [[1, 3, 2], [0, 4, 1], [3, 5, 4], [5, 4, 3]], // Example heatmap data
        tipoAccesoData: { web: 30, rest: 70 }
    });
    const [filtros, setFiltros] = useState({
        pais: '',
        fechaDesde: '',
        fechaHasta: '',
        tipoAcceso: ''
    });

    useEffect(() => {
        fetchDatos();
    }, []);

    const fetchDatos = async () => {
        try {
            const response = await axios.get('/api/analiticos/busquedas');
            setData(prev => ({ ...prev, busquedas: response.data }));
        } catch (error) {
            console.error('Error al cargar los datos de analítica:', error);
        }
    };

    const handleFiltroChange = (e) => {
        setFiltros({
            ...filtros,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmitFiltros = (e) => {
        e.preventDefault();
        fetchDatos(); 
    };






    const barOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
            }
        },
    };

    const lineOptions = {
        scales: {
            y: {
                beginAtZero: true
            }
        }
    };

    const pieOptions = {
        responsive: true,
        plugins: {
            legend: {
                position: 'right',
            }
        },
    };

    const barData = {
        labels: ['USA', 'Canada', 'UK', 'Germany', 'France'],
        datasets: [
            {
                label: 'Número de búsquedas por país',
                data: [65, 59, 80, 81, 56],
                backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
        ],
    };

    const lineData = {
        labels: ['January', 'February', 'March', 'April', 'May'],
        datasets: [
            {
                label: 'Búsquedas a lo largo del tiempo',
                data: [65, 59, 80, 81, 56],
                fill: false,
                borderColor: 'rgb(75, 192, 192)',
                tension: 0.1
            }
        ]
    };

    const pieData = {
        labels: ['Web', 'REST'],
        datasets: [
            {
                label: 'Tipo de Acceso',
                data: [data.tipoAccesoData.web, data.tipoAccesoData.rest],
                backgroundColor: [
                    'rgba(255, 205, 86, 0.7)',
                    'rgba(54, 162, 235, 0.7)'
                ],
                hoverOffset: 4
            }
        ]
    };

    return (
        <Container fluid>
            <Row>
                <Col md={12}>
                    <h2>Análisis de Búsquedas</h2>
                    <Form onSubmit={handleSubmitFiltros}>
                        <Row>
                            <Col>
                                <Form.Group controlId="filtroPais">
                                    <Form.Label>País</Form.Label>
                                    <Form.Control type="text" placeholder="Introduzca país" name="pais" value={filtros.pais} onChange={handleFiltroChange} />
                                </Form.Group>
                            </Col>
                            <Col>
                                <Form.Group controlId="filtroFechaDesde">
                                    <Form.Label>Fecha Desde</Form.Label>
                                    <Form.Control type="date" name="fechaDesde" value={filtros.fechaDesde} onChange={handleFiltroChange} />
                                </Form.Group>
                            </Col>
                            <Col>
                                <Form.Group controlId="filtroFechaHasta">
                                    <Form.Label>Fecha Hasta</Form.Label>
                                    <Form.Control type="date" name="fechaHasta" value={filtros.fechaHasta} onChange={handleFiltroChange} />
                                </Form.Group>
                            </Col>
                            <Col>
                                <Form.Group controlId="filtroTipoAcceso">
                                    <Form.Label>Tipo de Acceso</Form.Label>
                                    <Form.Control as="select" name="tipoAcceso" value={filtros.tipoAcceso} onChange={handleFiltroChange}>
                                        <option value="">Seleccione</option>
                                        <option value="web">Web</option>
                                        <option value="rest">REST</option>
                                    </Form.Control>
                                </Form.Group>
                            </Col>
                            <Col md={2} className="align-self-end">
                                <Button type="submit" className="w-100">Aplicar Filtros</Button>
                            </Col>
                        </Row>
                    </Form>
                </Col>
            </Row>
            <Row className="mt-4">
                <Col md={6}>
                    <Bar data={{
                        labels: ['USA', 'Canada', 'UK', 'Germany', 'France'],
                        datasets: [{
                            label: 'Número de búsquedas por país',
                            data: [65, 59, 80, 81, 56],
                            backgroundColor: 'rgba(255, 99, 132, 0.5)',
                        }],
                    }} options={{ responsive: true }} />
                </Col>
                <Col md={6}>
                    <Line data={{
                        labels: ['January', 'February', 'March', 'April', 'May'],
                        datasets: [{
                            label: 'Búsquedas a lo largo del tiempo',
                            data: [65, 59, 80, 81, 56],
                            fill: false,
                            borderColor: 'rgb(75, 192, 192)',
                            tension: 0.1
                        }]
                    }} options={{ scales: { y: { beginAtZero: true } } }} />
                </Col>
            </Row>
            <Row>
            <Col md={6}>
  <h4>Heatmap de Actividad</h4>
  <HeatMapGrid
    data={data.heatmapData}
    xLabels={["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"]}
    yLabels={["Week 1", "Week 2", "Week 3", "Week 4"]}
    cellStyle={(background, value, min, max, data, x, y) => ({
      background: `rgba(12, 160, 44, ${1 - (max - value) / (max - min)})`,
      fontSize: "11.5px",
    })}
    cellRender={value => value && `${value}`}
  />
</Col>

                <Col md={6}>
                    <h4>Distribución del Tipo de Acceso</h4>
                    <Pie data={{
                        labels: ['Web', 'REST'],
                        datasets: [{
                            label: 'Tipo de Acceso',
                            data: [30, 70],
                            backgroundColor: [
                                'rgba(255, 205, 86, 0.7)',
                                'rgba(54, 162, 235, 0.7)'
                            ],
                            hoverOffset: 4
                        }]
                    }} options={{ responsive: true }} />
                </Col>
            </Row>
        </Container>
    );
};

export default Analiticos;
