import React, { useState, useEffect } from 'react';

function FAQS() {
    const [faqs, setFaqs] = useState([]);
    const [seccionesGenerales, setSeccionesGenerales] = useState([]);
    const [faq, setFaq] = useState({ pregunta: '', respuesta: '' });
    const [seccion, setSeccion] = useState({ titulo: '', contenido: '' });
    const [isEditingFaq, setIsEditingFaq] = useState(null);
    const [isEditingSeccion, setIsEditingSeccion] = useState(null);

    useEffect(() => {
        fetchFaqs();
        fetchSeccionesGenerales();
    }, []);

    const handleResponse = async (response) => {
        if (!response.ok) {
            const message = `An error occurred: ${response.statusText}`;
            throw new Error(message);
        }
        const data = await response.json();
        return data;
    }

    const fetchFaqs = async () => {
        try {
            const response = await fetch('http://localhost:8080/faqs');
            const data = await handleResponse(response);
            setFaqs(data);
        } catch (error) {
            console.error("Fetching FAQs failed:", error);
        }
    };

    const fetchSeccionesGenerales = async () => {
        try {
            const response = await fetch('http://localhost:8080/secciones-generales');
            const data = await handleResponse(response);
            setSeccionesGenerales(data);
        } catch (error) {
            console.error("Fetching Secciones Generales failed:", error);
        }
    };

    const handleChange = (e, setState) => {
        const { name, value } = e.target;
        setState(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e, url, body, resetState, fetchFunction) => {
        e.preventDefault();
        try {
            const response = await fetch(url, {
                method: body.id ? 'PUT' : 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(body),
            });
            await handleResponse(response);
            resetState();
            fetchFunction();
        } catch (error) {
            console.error("Operation failed:", error);
        }
    };

    const handleDelete = async (id, url, fetchFunction) => {
        try {
            const response = await fetch(`${url}/${id}`, { method: 'DELETE' });
            await handleResponse(response);
            fetchFunction();
        } catch (error) {
            console.error("Delete operation failed:", error);
        }
    };

    return (
        <div>
            <h2>FAQs</h2>
            {faqs.map((faq) => (
                <div key={faq.id}>
                    <p>Pregunta: {faq.pregunta}</p>
                    <p>Respuesta: {faq.respuesta}</p>
                    <button onClick={() => editFaq(faq)}>Editar</button>
                    <button onClick={() => deleteFaq(faq.id)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={handleSubmitFaq}>
                <input name="pregunta" value={faq.pregunta} onChange={handleChangeFaq} placeholder="Pregunta" required />
                <input name="respuesta" value={faq.respuesta} onChange={handleChangeFaq} placeholder="Respuesta" required />
                <button type="submit">Guardar FAQ</button>
            </form>

            <h2>Secciones Generales</h2>
            {seccionesGenerales.map((seccion) => (
                <div key={seccion.id}>
                    <p>Título: {seccion.titulo}</p>
                    <p>Contenido: {seccion.contenido}</p>
                    <button onClick={() => editSeccion(seccion)}>Editar</button>
                    <button onClick={() => deleteSeccion(seccion.id)}>Eliminar</button>
                </div>
            ))}
            <form onSubmit={handleSubmitSeccion}>
                <input name="titulo" value={seccion.titulo} onChange={handleChangeSeccion} placeholder="Título" required />
                <textarea name="contenido" value={seccion.contenido} onChange={handleChangeSeccion} placeholder="Contenido" required />
                <button type="submit">Guardar Sección</button>
            </form>
        </div>
    );
}

export default FAQS;
