import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Analiticos = () => {
    const [barData, setBarData] = useState({
        labels: [],
        datasets: []
    });

    useEffect(() => {
        fetchDatos();
    }, []);

    const fetchDatos = async () => {
        try {
            const response = await axios.get('http://localhost:8080/analiticos/registros/paises');
            const data = response.data;
            setBarData({
                labels: Object.keys(data),
                datasets: [{
                    label: 'Número de búsquedas por país',
                    data: Object.values(data),
                    backgroundColor: 'rgba(255, 99, 132, 0.5)'
                }]
            });
        } catch (error) {
            console.error('Error al cargar los datos de analítica:', error);
        }
    };

    return (
        <Container fluid>
            <Row>
                <Col md={12}>
                    <h2>Número de Búsquedas por País</h2>
                    <Bar data={barData} options={{ responsive: true }} />
                </Col>
            </Row>
        </Container>
    );
};

export default Analiticos;
