import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };

  useEffect(() => {
    fetchHotelsAndRooms(); // Carga inicial sin filtro de país
  }, []);

  const fetchHotelsAndRooms = async (pais = '') => {
    // Ajuste para usar el nuevo endpoint que filtra por país
    let url = pais ? `http://localhost:8080/hoteles/por-pais/${pais}` : 'http://localhost:8080/hoteles';
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Error al cargar hoteles');
      }
      const hotelsData = await response.json();

      const hotelsWithRooms = await Promise.all(hotelsData.map(async (hotel) => {
        try {
          const roomsResponse = await fetch(`http://localhost:8080/habitaciones?hotelId=${hotel.id_hotel}`);
          if (!roomsResponse.ok) throw new Error('Failed to load rooms');
          const roomsData = await roomsResponse.json();
          return { ...hotel, rooms: roomsData };
        } catch (error) {
          console.error('Error fetching rooms for hotel:', hotel.id_hotel, error);
          return { ...hotel, rooms: [] };
        }
      }));

      setHotels(hotelsWithRooms);
    } catch (error) {
      console.error('Error fetching hotels:', error);
      setError(error.message);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchHotelsAndRooms(paisSeleccionado);
  };

  return (
    <Container className="my-5">
      <Row>
        <Col md={12}>
          <h2>Buscar Hoteles Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSearch}>
            <Form.Group controlId="pais">
              <Form.Label>País</Form.Label>
              <Form.Control as="select" value={paisSeleccionado} onChange={(e) => setPaisSeleccionado(e.target.value)}>
                <option value="">Seleccione un país</option>
                {/* Opciones de países. Si tienes una lista fija, aquí irían. */}
                {/* Si no, considera cargar esta lista desde el servidor al iniciar. */}
                <option value="Argentina">Argentina</option>
                <option value="Brasil">Brasil</option>
                <option value="Chile">Chile</option>
                {/* Añade más opciones según sea necesario */}
              </Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit">Buscar</Button>
          </Form>
        </Col>
      </Row>
      {/* Resto del componente para mostrar hoteles */}
      <Row>
        {hotels.length > 0 ? (
          hotels.map(hotel => (
            <React.Fragment key={hotel.id_hotel}>
              {/* Detalles del hotel y habitaciones */}
            </React.Fragment>
          ))
        ) : (
          <Col>
            <p className="mt-4">No se encontraron hoteles. Intente con otro país.</p>
          </Col>
        )}
      </Row>
    </Container>
  );
};

export default HomePage;
