import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg'; // Asegúrate de que esta ruta es correcta para la imagen por defecto
import Comentarios from './Comentarios';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [fechaIngreso, setFechaIngreso] = useState('');
  const [fechaSalida, setFechaSalida] = useState('');
  const [numeroPersonas, setNumeroPersonas] = useState(1);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [hotelImages, setHotelImages] = useState({});

  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };

  useEffect(() => {
    fetchPaises();
  }, []);

  const fetchHotelImages = async (idHotel) => {
    try {
      const response = await fetch(`http://localhost:8080/hoteles/${idHotel}/imagenes`);
      if (!response.ok) throw new Error('Error al cargar imágenes del hotel');
      const images = await response.json();
      setHotelImages(prevImages => ({ ...prevImages, [idHotel]: images }));
    } catch (error) {
      console.error('Error fetching hotel images:', error);
    }
  };

  const fetchPaises = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles/pais');
      if (!response.ok) throw new Error('Error al cargar los países');
      const data = await response.json();
      setPaises(data);
    } catch (error) {
      setError('Error al cargar los países: ' + error.message);
    }
  };

  const fetchHotelsAndRooms = async () => {
    let url = `http://localhost:8080/habitaciones/buscar?pais=${paisSeleccionado}&fechaIngreso=${fechaIngreso}&fechaSalida=${fechaSalida}&numeroPersonas=${numeroPersonas}`;
    try {
      const response = await fetch(url);
      if (!response.ok) throw new Error('No se encontraron habitaciones disponibles para los parámetros dados.');
      const data = await response.json();
      // Asumiendo que la respuesta incluye información del hotel y las habitaciones
      setHotels(data); // Asegúrate de que esta línea coincida con la estructura de tu respuesta
    } catch (error) {
      setError(error.message);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchHotelsAndRooms();
  };

  return (
    <Container className="my-5">
      <Row>
        <Col md={12}>
          <h2>Buscar Hoteles Disponibles</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Row>
              <Col>
                <Form.Group controlId="pais">
                  <Form.Label>País</Form.Label>
                  <Form.Control as="select" value={paisSeleccionado} onChange={(e) => setPaisSeleccionado(e.target.value)}>
                    <option value="">Seleccione un país</option>
                    {paises.map((pais, index) => (
                      <option key={index} value={pais}>{pais}</option>
                    ))}
                  </Form.Control>
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="fechaIngreso">
                  <Form.Label>Fecha de Ingreso</Form.Label>
                  <Form.Control type="date" value={fechaIngreso} onChange={(e) => setFechaIngreso(e.target.value)} />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="fechaSalida">
                  <Form.Label>Fecha de Salida</Form.Label>
                  <Form.Control type="date" value={fechaSalida} onChange={(e) => setFechaSalida(e.target.value)} />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="numeroPersonas">
                  <Form.Label>Número de Personas</Form.Label>
                  <Form.Control type="number" value={numeroPersonas} onChange={(e) => setNumeroPersonas(e.target.value)} />
                </Form.Group>
              </Col>
              <Col className="d-flex align-items-end">
                <Button type="submit" className="mb-3">Buscar</Button>
              </Col>
            </Row>
          </Form>
        </Col>
      </Row>
      <Row>
        {hotels.length > 0 ? (
          hotels.map((hotel) => (
            <Col md={4} key={hotel.id}>
              <Card>
                <Card.Img variant="top" src={hotel.imagen || defaultRoomImage} />
                <Card.Body>
                  <Card.Title>{hotel.nombre}</Card.Title>
                  <Card.Text>{hotel.direccion}</Card.Text>
                  <Button variant="primary" onClick={() => navigate(`/hotel/${hotel.id}`)}>Ver detalles</Button>
                </Card.Body>
              </Card>
            </Col>
          ))
        ) : <Alert variant="info">No se encontraron hoteles disponibles.</Alert>}
      </Row>
    </Container>
  );
};

export default HomePage;
