import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import HotelSearchForm from './HotelSearchForm';

const HomePage = () => {
  // Asegúrate de que este bloque de código permanezca en HomePage.jsx
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();
  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };

  useEffect(() => {
    // Inicia una carga de hoteles sin filtro inicialmente.
    fetchHotelsAndRooms();
  }, []);

  const fetchHotelsAndRooms = async (pais = '') => {
    let url = `http://localhost:8080/hoteles${pais ? `?pais=${encodeURIComponent(pais)}` : ''}`;
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Error al cargar hoteles');
      }
      const hotelsData = await response.json();

      const hotelsWithRooms = await Promise.all(hotelsData.map(async (hotel) => {
        try {
          const roomsResponse = await fetch(`http://localhost:8080/habitaciones?hotelId=${hotel.id_hotel}`);
          if (!roomsResponse.ok) {
            throw new Error('Error al cargar habitaciones');
          }
          const roomsData = await roomsResponse.json();
          return { ...hotel, rooms: roomsData };
        } catch (error) {
          console.error('Error fetching rooms for hotel:', hotel.id_hotel, error);
          return { ...hotel, rooms: [] }; // En caso de error, sigue incluyendo el hotel pero sin habitaciones.
        }
      }));

      setHotels(hotelsWithRooms);
    } catch (error) {
      console.error('Error fetching hotels:', error);
    }
  };

  const handleSearch = (pais) => {
    fetchHotelsAndRooms(pais);
  };

  return (
    <Container className="my-5">
      <HotelSearchForm onSearch={handleSearch} />
      {hotels.length > 0 ? (
        hotels.map(hotel => (
          <React.Fragment key={hotel.id_hotel}>
            <Row className="mb-3">
              <Col md={12}>
                <h2>Hotel: {hotel.nombre}</h2>
                <p>{hotel.ciudad}, {hotel.pais}</p>
                <p>Dirección: {hotel.direccion}</p>
              </Col>
              {hotel.rooms && hotel.rooms.length > 0 && hotel.rooms.map(room => (
                <Col key={room.id_habitacion} md={4}>
                  <Card className="mb-3">
                    <Card.Img variant="top" src={defaultRoomImage || 'placeholder-image-url'} />
                    <Card.Body>
                      <Card.Title>Habitación: {room.tipo_habitacion && tiposHabitacion[room.tipo_habitacion]}</Card.Title>
                      <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                      <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                      <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                      <Card.Text>Valoración: {room.valuacion} estrellas</Card.Text>
                      <Button variant="primary" onClick={() => navigate('/checkout', { state: { hotelDetails: hotel, roomDetails: room } })}>Reservar</Button>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>
          </React.Fragment>
        ))
      ) : (
        <p>No se encontraron hoteles. Por favor, intenta nuevamente con diferentes criterios de búsqueda.</p>
      )}
    </Container>
  );
};

export default HomePage;
