import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Image, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import defaultRoomImage from './roomImage.jpg';
import Comentarios from './Comentarios';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [paises, setPaises] = useState([]);
  const [paisSeleccionado, setPaisSeleccionado] = useState('');
  const [fechaIngreso, setFechaIngreso] = useState('');
  const [fechaSalida, setFechaSalida] = useState('');
  const [numeroPersonas, setNumeroPersonas] = useState(1);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [hotelImages, setHotelImages] = useState({});
  const [roomTypes, setRoomTypes] = useState({});




  const tiposHabitacion = {
    1: 'Doble',
    2: 'Junior Suite',
    3: 'Suite',
    4: 'Gran Suite'
  };


  useEffect(() => {
    fetchRoomTypes();
    fetchPaises();
    fetchHotelsAndRooms(paisSeleccionado);
  }, []);





  //IMAGENES

  const fetchHotelImages = async (idHotel) => {
    try {
      const response = await fetch(`http://localhost:8080/hoteles/${idHotel}/imagenes`);
      if (!response.ok) throw new Error('Error al cargar imágenes del hotel');
      const images = await response.json();
      setHotelImages(prevImages => ({ ...prevImages, [idHotel]: images }));
    } catch (error) {
      console.error('Error fetching hotel images:', error);
    }
  };

  useEffect(() => {
    hotels.forEach((hotel) => {
      fetchHotelImages(hotel.id_hotel);
    });
  }, [hotels]);



  const fetchRoomTypes = async () => {
    try {
      const response = await fetch('http://localhost:8080/tipos_habitacion');
      if (!response.ok) throw new Error('Error al cargar los tipos de habitación');
      const data = await response.json();
      setRoomTypes(data.reduce((map, roomType) => {
        map[roomType.id_tipo] = roomType.imagenUrl || defaultRoomImage; 
        return map;
      }, {}));
    } catch (error) {
      setError('Error al cargar los tipos de habitación: ' + error.message);
    }
  };




  const fetchPaises = async () => {
    try {
      const response = await fetch('http://localhost:8080/hoteles/pais');
      if (!response.ok) throw new Error('Error al cargar los países');
      const data = await response.json();
      setPaises(data);
    } catch (error) {
      setError('Error al cargar los países: ' + error.message);
    }
  };

  const fetchHotelsAndRooms = async (pais = '', fechaIngreso = '', fechaSalida = '', numeroPersonas = 1) => {
    // Asegurando que los parámetros se envían correctamente al backend
    let url = `http://localhost:8080/habitaciones/buscar?`;
    const params = new URLSearchParams();

    // Añadiendo los parámetros solo si están presentes
    if (pais) params.append('pais', pais);
    if (fechaIngreso) params.append('fechaIngreso', fechaIngreso);
    if (fechaSalida) params.append('fechaSalida', fechaSalida);
    params.append('numeroPersonas', numeroPersonas);

    // Completando la URL con los parámetros de búsqueda
    url += params.toString();

    try {
      const response = await fetch(url);
      if (!response.ok) {
        // Lanzando un error con el statusText para una mejor descripción del problema
        throw new Error(`Error al cargar hoteles y habitaciones: ${response.statusText}`);
      }
      const roomsData = await response.json();

      // Organizar las habitaciones por hotel utilizando la función organizeRoomsByHotel
      const organizedHotels = organizeRoomsByHotel(roomsData);
      setHotels(organizedHotels);
    } catch (error) {
      console.error('Error fetching filtered hotels and rooms:', error);
      setError('Error al cargar hoteles y habitaciones con los filtros aplicados: ' + error.message);
    }
};


  const handleSearch = (e) => {
    e.preventDefault();
    // Restablece el estado de error antes de hacer una nueva búsqueda para limpiar errores anteriores
    setError('');
    fetchHotelsAndRooms(paisSeleccionado, fechaIngreso, fechaSalida, numeroPersonas);
  };




  const organizeRoomsByHotel = (roomsData) => {
    const hotels = {};

    roomsData.forEach(room => {
        if (!hotels[room.id_hotel]) {
            hotels[room.id_hotel] = {
                id_hotel: room.id_hotel,
                rooms: [],
                // Aquí asumimos que podrías tener más datos del hotel para agregar
            };
        }
        hotels[room.id_hotel].rooms.push({
            id_habitacion: room.id_habitacion,
            numero_habitacion: room.numero_habitacion,
            capacidad_personas: room.capacidad_personas,
            tipo_habitacion: room.tipo_habitacion,
            precioxpersona: room.precioxpersona,
            precioxnoche: room.precioxnoche,
            valuacion: room.valuacion,
            disponible: room.disponible, // Asumiendo que este campo indica si la habitación está disponible
        });
    });

    return Object.values(hotels); // Convertir el objeto en un array de hoteles
};




  return (
    <Container className="my-5">
      <Row>
        <Col md={12}>
          {/* Formulario para los filtros de búsqueda */}
          <Form onSubmit={handleSearch}>
            <Row>
              <Col>
                <Form.Group controlId="formPais">
                  <Form.Label>País</Form.Label>
                  <Form.Control as="select" value={paisSeleccionado} onChange={(e) => setPaisSeleccionado(e.target.value)}>
                    {paises.map((pais) => (
                      <option key={pais} value={pais}>{pais}</option>
                    ))}
                  </Form.Control>
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="formFechaIngreso">
                  <Form.Label>Fecha de Ingreso</Form.Label>
                  <Form.Control type="date" value={fechaIngreso} onChange={(e) => setFechaIngreso(e.target.value)} />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="formFechaSalida">
                  <Form.Label>Fecha de Salida</Form.Label>
                  <Form.Control type="date" value={fechaSalida} onChange={(e) => setFechaSalida(e.target.value)} />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="formNumeroPersonas">
                  <Form.Label>Número de Personas</Form.Label>
                  <Form.Control type="number" value={numeroPersonas} onChange={(e) => setNumeroPersonas(e.target.value)} />
                </Form.Group>
              </Col>
            </Row>
            <Button variant="primary" type="submit">
              Buscar
            </Button>
          </Form>
        </Col>
      </Row>
      <Row>
      {hotels.length > 0 ? (
  hotels.map((hotel) => (
    <React.Fragment key={hotel.id_hotel}>
      <Col md={10} className="mt-4">
        <h3>Hotel: {hotel.nombre}</h3>
        <p>{hotel.ciudad}, {hotel.pais}</p>
        <p>Dirección: {hotel.direccion}</p>
        {hotelImages[hotel.id_hotel] && hotelImages[hotel.id_hotel].length > 0 && (
          <Carousel interval={null}>
            {hotelImages[hotel.id_hotel].map((image, index) => (
              <Carousel.Item key={index}>
                <img
                  className="d-block w-100"
                  src={image}
                  alt={`Imagen ${index + 1} del hotel ${hotel.nombre}`}
                />
              </Carousel.Item>
            ))}
          </Carousel>
        )}

      </Col>
              {hotel.rooms && hotel.rooms.map((room) => (
                <Col key={room.id_habitacion} md={6}>
                  <Card className="mb-3">
                  <Card.Img variant="top" src={room.imagenUrl || defaultRoomImage} />
                    <Card.Body>
                    <Card.Title>Habitación: {tiposHabitacion[room.tipo_habitacion]}</Card.Title>
                      <Card.Text>Número de habitación: {room.numero_habitacion}</Card.Text>
                      <Card.Text>Capacidad máxima: {room.capacidad_personas} personas</Card.Text>
                      <Card.Text>Precio por noche: ${room.precioxnoche}</Card.Text>
                      <Card.Text>Valoración: {room.valuacion} estrellas</Card.Text>
                      <Button variant="primary" onClick={() => {
                      console.log("Navigating with hotelDetails:", hotel);
                      navigate('/checkout', {
                        state: {
                          hotelDetails: { ...hotel },
                          roomDetails: {
                            ...room,
                            idHabitacion: room.id_habitacion,
                            roomType: tiposHabitacion[room.tipo_habitacion],
                            roomPrice: room.precioxnoche,
                            capacidadPersonas: room.capacidad_personas
                          }
                        }
                      });
                    }}>
                      Reservar
                    </Button>

                    <Comentarios idHabitacion={room.id_habitacion} />


                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </React.Fragment>
          ))
        ) : (
          <Col>
            <p className="mt-4">No se encontraron hoteles. Por favor, intenta nuevamente con diferentes criterios de búsqueda.</p>
          </Col>
        )}
      </Row>
    </Container>
  );
};

export default HomePage;
