import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, PointElement, LineElement, ArcElement } from 'chart.js';
import { Bar, Line, Pie } from 'react-chartjs-2';
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  PointElement,
  LineElement,
  ArcElement
);

const Analiticos = () => {
    const [barChartData, setBarChartData] = useState({});
    const [lineChartData, setLineChartData] = useState({});
    const [pieChartData, setPieChartData] = useState({});

    useEffect(() => {
        const fetchData = async () => {
            const requests = {
                paises: axios.get('http://localhost:8080/analiticos/registros/paises'),
                evolucion: axios.get('http://localhost:8080/analiticos/registros/evolucion'),
                tipoAcceso: axios.get('http://localhost:8080/analiticos/registros/tipoacceso')
            };

            try {
                const { paises, evolucion, tipoAcceso } = await axios.all([
                    requests.paises,
                    requests.evolucion,
                    requests.tipoAcceso
                ]);

                // Procesar los datos para la gráfica de barras
                setBarChartData({
                    labels: Object.keys(paises.data),
                    datasets: [{
                        label: 'Número de búsquedas por país',
                        data: Object.values(paises.data),
                        backgroundColor: 'rgba(255, 99, 132, 0.5)',
                    }],
                });

                // Procesar los datos para la gráfica de líneas
                setLineChartData({
                    labels: Object.keys(evolucion.data),
                    datasets: [{
                        label: 'Búsquedas a lo largo del tiempo',
                        data: Object.values(evolucion.data),
                        fill: false,
                        borderColor: 'rgb(75, 192, 192)',
                        tension: 0.1,
                    }],
                });

                // Procesar los datos para la gráfica de pie
                setPieChartData({
                    labels: ['Web', 'REST'],
                    datasets: [{
                        label: 'Tipo de Acceso',
                        data: [tipoAcceso.data.web, tipoAcceso.data.rest],
                        backgroundColor: [
                            'rgba(255, 205, 86, 0.7)',
                            'rgba(54, 162, 235, 0.7)'
                        ],
                        hoverOffset: 4
                    }],
                });
            } catch (error) {
                console.error('Error al cargar los datos:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <Container fluid>
            <Row>
                <Col md={12}>
                    <h2>Análisis de Búsquedas por País</h2>
                    {barChartData.labels && <Bar data={barChartData} options={{ responsive: true }} />}
                </Col>
            </Row>
            <Row>
                <Col md={12}>
                    <h2>Evolución de las Búsquedas</h2>
                    {lineChartData.labels && <Line data={lineChartData} options={{ responsive: true, scales: { y: { beginAtZero: true } } }} />}
                </Col>
            </Row>
            <Row>
                <Col md={12}>
                    <h2>Distribución del Tipo de Acceso</h2>
                    {pieChartData.labels && <Pie data={pieChartData} options={{ responsive: true }} />}
                </Col>
            </Row>
        </Container>
    );
};

export default Analiticos;
