import React, { useState, useEffect } from 'react';

function FAQs() {
  const [faqs, setFaqs] = useState([]);
  const [seccionesGenerales, setSeccionesGenerales] = useState([]);
  const [faqEdit, setFaqEdit] = useState({ pregunta: '', respuesta: '', id: null });
  const [seccionEdit, setSeccionEdit] = useState({ titulo: '', contenido: '', id: null });
  const [openDialog, setOpenDialog] = useState(false);
  const [deleteFunction, setDeleteFunction] = useState(null);

  useEffect(() => {
    fetchFaqs();
    fetchSeccionesGenerales();
  }, []);

    // Fetch FAQs
    async function fetchFaqs() {
        const response = await fetch('http://localhost:8080/faqs');
        const data = await response.json();
        setFaqs(data);
    }

    // Fetch Secciones Generales
    async function fetchSeccionesGenerales() {
        const response = await fetch('http://localhost:8080/secciones-generales');
        const data = await response.json();
        setSeccionesGenerales(data);
    }

    // Handle change for FAQs and Secciones Generales
    function handleChange(e, setFunction) {
        const { name, value } = e.target;
        setFunction(prevState => ({ ...prevState, [name]: value }));
    }

    // Submit FAQ
    async function handleSubmitFaq(e) {
        e.preventDefault();
        const url = faqEdit.id ? `http://localhost:8080/faqs/${faqEdit.id}` : 'http://localhost:8080/faqs';
        const method = faqEdit.id ? 'PUT' : 'POST';

        await fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(faqEdit),
        });

        setFaqEdit({ pregunta: '', respuesta: '', id: null }); // Reset form
        fetchFaqs(); // Refetch FAQs
    }

    // Submit Sección General
    async function handleSubmitSeccion(e) {
        e.preventDefault();
        const url = seccionEdit.id ? `http://localhost:8080/secciones-generales/${seccionEdit.id}` : 'http://localhost:8080/secciones-generales';
        const method = seccionEdit.id ? 'PUT' : 'POST';

        await fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(seccionEdit),
        });

        setSeccionEdit({ titulo: '', contenido: '', id: null }); // Reset form
        fetchSeccionesGenerales(); // Refetch Secciones Generales
    }

    // Delete FAQ
    async function deleteFaq(id) {
        await fetch(`http://localhost:8080/faqs/${id}`, { method: 'DELETE' });
        fetchFaqs(); // Refetch FAQs
    }

    // Delete Sección General
    async function deleteSeccion(id) {
        await fetch(`http://localhost:8080/secciones-generales/${id}`, { method: 'DELETE' });
        fetchSeccionesGenerales(); // Refetch Secciones Generales
    }




    const handleOpenDialog = (fn) => {
      setOpenDialog(true);
      setDeleteFunction(() => fn);
    };

    const handleCloseDialog = () => {
      setOpenDialog(false);
    };



return (
    <Container maxWidth="md">
        <Typography variant="h4" gutterBottom>FAQs y Secciones Generales</Typography>

        <Box my={4}>
            <Typography variant="h5" gutterBottom>FAQs</Typography>
            {faqs.map(faq => (
                <Paper key={faq.id} elevation={2} sx={{ p: 2, mb: 2 }}>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <Typography>{faq.pregunta}</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography>{faq.respuesta}</Typography>
                        </Grid>
                        <Grid item>
                            <Button variant="contained" color="primary" onClick={() => setFaqEdit(faq)}>Editar</Button>
                            <Button variant="outlined" color="secondary" sx={{ ml: 1 }} onClick={() => handleDialogOpen(() => deleteFaq(faq.id))}>Eliminar</Button>
                        </Grid>
                    </Grid>
                </Paper>
            ))}
            <form onSubmit={handleSubmitFaq}>
                <TextField
                    name="pregunta"
                    value={faqEdit.pregunta}
                    onChange={(e) => handleChange(e, setFaqEdit)}
                    placeholder="Pregunta"
                    fullWidth
                    margin="normal"
                />
                <TextField
                    name="respuesta"
                    value={faqEdit.respuesta}
                    onChange={(e) => handleChange(e, setFaqEdit)}
                    placeholder="Respuesta"
                    fullWidth
                    margin="normal"
                />
                <Button type="submit" color="primary" variant="contained">Guardar FAQ</Button>
            </form>
        </Box>

        <Box my={4}>
            <Typography variant="h5" gutterBottom>Secciones Generales</Typography>
            {seccionesGenerales.map(seccion => (
                <Paper key={seccion.id} elevation={2} sx={{ p: 2, mb: 2 }}>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <Typography>{seccion.titulo}</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography>{seccion.contenido}</Typography>
                        </Grid>
                        <Grid item>
                            <Button variant="contained" color="primary" onClick={() => setSeccionEdit(seccion)}>Editar</Button>
                            <Button variant="outlined" color="secondary" sx={{ ml: 1 }} onClick={() => handleDialogOpen(() => deleteSeccion(seccion.id))}>Eliminar</Button>
                        </Grid>
                    </Grid>
                </Paper>
            ))}
            <form onSubmit={handleSubmitSeccion}>
                <TextField
                    name="titulo"
                    value={seccionEdit.titulo}
                    onChange={(e) => handleChange(e, setSeccionEdit)}
                    placeholder="Título"
                    fullWidth
                    margin="normal"
                />
                <TextField
                    name="contenido"
                    value={seccionEdit.contenido}
                    onChange={(e) => handleChange(e, setSeccionEdit)}
                    placeholder="Contenido"
                    fullWidth
                    multiline
                    rows={4}
                    margin="normal"
                />
                <Button type="submit" color="primary" variant="contained">Guardar Sección</Button>
            </form>
        </Box>

        <Dialog open={dialogOpen} onClose={handleDialogClose}>
            <DialogTitle>Confirmación</DialogTitle>
            <DialogContent>
                <DialogContentText>¿Estás seguro de que quieres eliminar este elemento?</DialogContentText>
            </DialogContent>
            <DialogActions>
                <Button onClick={handleDialogClose} color="primary">Cancelar</Button>
                <Button onClick={() => handleDelete(deleteAction)} color="secondary">Eliminar</Button>
            </DialogActions>
        </Dialog>
    </Container>
);
}
export default FAQs;
